css:
@-moz-document domain("ministrybooks.org") {
#ministry, #top {
 	background-color: #333;
    color: white;
    background-image: none;
    overflow: inherit;
    font-family: sans-serif;
}

#ministry-text + hr + .section-nav {
    margin-bottom: 600px;
}

#top {
    padding-top: 0px !important;
}

html {
    background-color: #333;
}

table, table tr:nth-of-type(even) {
    background: #333;
	border: none;
}

div#footer-links, #footer, .row + .row, .breadcrumbs, [data-section='accordion'], .section-container.accordion {
    display: none;
}

#ministry-text .decimalbd {
	color: white;
}

.fixed {
 position: inherit;
}

h1,h2,h3,h4,h5, .subheader, #ministry-text h2, #ministry-text H2, #ministry-text .head1, #ministry-text .HEAD1, #ministry-text .head2, #ministry-text HEAD2, #ministry-text HEAD2F, #ministry-text .title, #ministry-text .TITLE, #ministry-text .paren, #ministry-text .PAREN, #ministry-text .msg, #ministry-text .MSG, #ministry-text .subtitle, #ministry-text .SUBTITLE {
    color: white;
}
a:hover {
    color: lightgrey;
}

.breadcrumbs>* a, .breadcrumbs>* span {
    color: white;
}

.book-pos {
    color: white;
}

}
