css:
@-moz-document domain("www.tumblr.com") {
Body {
    background: url("http://puu.sh/oT9lU/5c7e4b7c4b.jpg") no-repeat fixed !important;
    background-color: rgba(0, 0, 0, 1) !important;
    background-position: 0% 0% !important;
    background-size: cover !important;
}

.post_container, article {
	box-shadow: 2px 2px 3px #000000;
}

.post_controls .post_control.messaging:before,
.post_controls .post_control.reblog:before,
.post_controls .post_control.post-control-icon:before {
	color:#556C8B;
}

.identity .controls_section.user_list li .follow_list_item_blog:before {
    display: none;
}
.post_avatar {
    background-color: transparent!important
}
#left_column,
#right_column {
    background-color: transparent !important;
}
div.l-content.l-content.l-content {
    background: rgba(0, 0, 0, 0) !important;
}
.l-container.l-container--two-column-dashboard .left_column {
    background-color: rgba(0, 0, 0, 0) !important;
}
#logo {
    background: rgba(25, 190, 255, 0)!important;
    border-radius: 5px !important;
}
.tab_bar .tab.iconic .tab_anchor {
    background-color: rgba(25, 190, 255, 0) !important;
    border-radius: 20px !important;
}
DIV.l-header-container {
    background-color: transparent!important;
    box-shadow: none!important;
    position: fixed !important;
}

DIV.tab.iconic .tab_notice .tab_notice_value {
    color: #000 !important;
}
.mceContentBody {
    background: rgb(255, 255, 255) !important;
    color: rgb(68, 68, 68) !important;
}
#new_post {
    opacity: 1 !important;
}
#new_post_label_text {
    color: rgba(62, 62, 64, 1) !important;
}
#new_post_label_photo {
    color: rgba(194, 85, 60, 1) !important;
}
#new_post_label_quote {
    color: rgba(218, 138, 45, 1) !important;
}
#new_post_label_link {
    color: rgba(79, 171, 130, 1) !important;
}
#new_post_label_chat {
    color: rgba(74, 142, 187, 1) !important;
}
#new_post_label_audio {
    color: rgba(151, 113, 179, 1) !important;
}
#new_post_label_video {
    color: rgba(105, 116, 127, 1) !important;
}
#posts .post .post_content {
    background: transparent !important;
}
#posts .post {
    background: rgba(0, 0, 0, .3) !important;
    border-radius: 0px !important;
}
.post_footer {
    background-color: rgba(0, 0, 0, 0) !important;
    border-radius: 20px !important;
}
.tracked_tags {
    background-color: rgba(255, 255, 255, 0) !important;
    border-radius: 0px !important;
}
.popover--account-popover .popover.popover--active {
	background:rgba(0,0,0,0.5) !important;
    opacity:0.8;
}

.note_item {
    background-color: rgba(5, 5, 20, .6)!important;
    border: 0px dashed !important;
    border-color: #5CB4D6!important;
}
.post.fan_mail {
    color: #000 !important;
}
.quote {
    color: #fff !important;
}
.link_text_container {
    background-color: #000 !important;
}
.plexi.color.active.show {
    background-color: rgba(0, 0, 0, .4) !important;
}
.drop_target {
    background-color: rgba(5, 5, 20, .7) !important;
}
.drop_zone {
    border: solid 2px rgba(65, 65, 250, 1) !important;
    border-radius: 2px !important;
}
#tumblelog_select > .txt {
    color: rgba(224, 224, 224, 1) !important;
}
#tumblelog_select > .txt:hover,
.photo_field.drop_zone_container .drop_zone .url_button {
    color: rgba(224, 224, 224, 1) !important;
}
.chrome.flat.close:hover,
.photo_field.drop_zone_container .drop_zone .url_button:hover {
    color: rgba(224, 224, 224, 1) !important;
}
#tumblelog_select > .txt:hover:after,
.post_options_wrapper .cog:hover,
.chrome.blue.options {
    opacity: 1 !important;
}
#tumblelog_select > .txt:after,
.post_options_wrapper .cog {
    opacity: 1 !important;
}
pre,
pre a,
pre SPAN,
.quote_source pre,
div pre,
pre SPAN,
pre a,
CODE,
pre small {
    background: rgba(0, 0, 0, .4)!important;
    font: bold .9em "arial", consolas, courier!important;
    white-space: pre-wrap!important;
    white-space: -moz-pre-wrap!important;
    white-space: -pre-wrap!important;
    white-space: -o-pre-wrap!important;
    word-wrap: break-word!important
}
.return_to_top_icon {
    background: url('http://i.imgur.com/PUxSi9B.png')!important;
    width: 50px!important;
    height: 50px !important;
    position: absolute!important;
    margin-top: -10px!important;
    margin-left: -40px !important;
}
.chat_line:nth-child(even),
PRE {
    background-color: rgba(255, 255, 255, 0)!important;
    color: rgba(224, 224, 224, 1)!important
}
.answer_container,
.notes LI {
    background: rgba(2, 1, 8, 1)!important;
}
li.selected,
li.recessed,
a.active,
div.post_filter_tabs a {
    background: none !important;
}
.post.photo_post .photo,
.post.photo_post .video_thumbnail {
    box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
    border-radius: 12px !important;
}
.content.is_media,
.mask.top,
.mask.bottom {
    background: none !important;
}
#content_top.content_cap,
#content_bottom.content_cap {
    visibility: hidden !important;
}
div#edit_post_controls {
    top: 20px !important;
}
#dashboard_edit_post #left_column,
#right_column {
    background-color: transparent !important;
}
div.l-content.l-content.l-content {
    border-radius: 14px !important;
}
#auto_pagination_loader {
    background: rgba(0, 0, 0, .5) !important;
    border-radius: 10px !important;
}
.post_content,
.new_post_label,
#right_column > UL:first-child > LI:first-child > A > SMALL > DIV,
.post_controls A,
.post_info,
.post_info A,
.footer_links.with_tags.with_source_url A,
.footer_links A,
.footer_links,
.block,
#dashboard_controls A,
#dashboard_controls DIV SPAN,
.add_and_remove,
.notification.single_notification,
.notification,
.notification .hide_overflow A,
.notification.last_notification,
.notification.last_notification A,
.answer_container SPAN,
.notes,
.notes LI,
.notes_hide_link A,
.read_more_container,
#footer,
#footer DIV A,
.post_question_asker,
.source_url,
.label_and_follow,
.follow,
#search_query,
#search_controls_button,
.posts,
.followers,
.messages,
.drafts,
.queue,
.flat.no_hover DIV,
A[class^="category"],
a:link,
a[href^="http"],
a[href^="/edit"],
a[href^="/likes"],
a[href^="/new"],
a[href^="/tagged"],
a[href^="/login"],
a[href^="/docs"],
a[href^="/explore"],
a[href^="/mega-editor"],
.count,
.customize,
.settings,
a span[id^="note_link"],
.answer_container,
.no_posts_found,
.following,
H1[id="page_heading"],
div.l-content P,
H1,
p SMALL,
.notes_loader,
.small_links A .hide_overflow,
.sorry P,
.cell.lead_editor,
.sidebar_nav_item A,
.tag,
H2,
#posts .post .post_controls .reblog_count span {
    color: rgba(224, 224, 224, 1) !important;
}
.post_notes {
    color: rgba(224, 224, 224, 1) !important;
}

strong {
    font-weight: bold !important;
    color: #fff !important;
}

.source_url_gradient,
#toggle_reasons {
    display: none !important;
}
.notification.single_notification A,
.notification.first_notification A,
.notification A,
.notification.last_notification A,
.notification .hide_overflow A {
    border-bottom: solid thin rgba(64, 64, 64, 1.0) !important;
}
.post_content P A,
.post_title A {
    color: rgba(232, 232, 232, 1) !important;
}
.tag {
    color: rgba(232, 232, 232, 1)!important;
}
.post_tags:after {
    visibility: hidden!important;
}

.post_controls .post_control:before {
	line-height:23.8px;
    text-shadow:1px 1px 1px #000000;
}

.post_content DIV:last-child, BLOCKQUOTE {
	border-color:#000000;
}

.post .post-source-footer {
	background:none;
     box-shadow: 
         0px 11px 5px -10px #CCC,
         0px -11px 4px -10px #CCC; 
    width:100%;
}

.identity-refresh .post_full .post_permalink {
    border-color: transparent!important;
    border-radius: 10px!important
}
#tumblr_radar {
    display: none !important;
}
#sidebar_footer_nav {
    display: none !important;
}
#right_column .controls_section li a,
#right_column {
    color: #E0E0E0 !important;
    background: rgba(0, 0, 0, 0)!important;
    border-radius: 10px !important;
}
#left_column.followers {
    background: rgba(0, 0, 0, 0) !important;
}
.left_column .white_pane {
    background: rgba(0, 0, 0, .4) !important;
}
#left_column {
    background: rgba(0, 0, 0, 0) !important;
}
.notifications_page .notification_pane {
    background: rgba(0, 0, 0, .5) !important;
    -webkit-border-radius: 0 !important;
    -moz-border-radius: 0 !important;
    border-radius: 0 !important;
    -webkit-box-shadow: none !important;
    -moz-box-shadow: none !important;
    box-shadow: none !important;
    font-size: 15px !important;
    position: relative !important;
    letter-spacing: 0 !important;
}

.ui_notes .ui_note.is_friend {
    color: #fff!important;
    border-bottom: 0px solid #4877ab !important;
    border-top: 0px solid #4877ab !important;
    margin-top: -1px !important;
}
.ui_notes .date_header,
.date_header {
    color: #000 !important;
    line-height: 22px !important;
    font-size: 11px !important;
    padding: 0 10px !important;
}
#x1cpostage_remove_caption {
    background: #000 !important;
}
#x1cpostage_caption {
    background: #000 !important;
    color: #E0E0E0 !important;
}
#x1cpostage_replace {
    background: #000 !important;
}
#x1cpostage_tags {
    background: #000 !important;
    color: #E0E0E0 !important;
}
#x1cpostage_blog {
    background: #000 !important;
    color: #E0E0E0 !important;
}
.notification.single_notification,
.notification.first_notification,
.notification.last_notification,
.notification {
    background: none !important;
    border: none !important;
}
.notification.single_notification,
.notification.first_notification,
.notification.last_notification,
.notification,
.notification.single_notification A,
.notification.first_notification A,
.notification A,
.notification.last_notification A,
.notification .hide_overflow A {
    color: rgba(224, 224, 224, 1) !important;
}
.notification.single_notification A,
.notification.first_notification A,
.notification A,
.notification.last_notification A,
.notification .hide_overflow A {
    border-bottom: solid thin rgba(101, 28, 13, 0) !important;
}
.notification.single_notification A:hover,
.notification.first_notification A:hover,
.notification A:hover,
.notification.last_notification A:hover,
.notification .hide_overflow A:hover {
    color: rgba(255, 0, 0, 0) !important;
}
ol#posts li.notification.alt {
    background: rgba(0, 0, 0, .4) !important;
    border-radius: 5px!important;
}
#content_top,
#content_bottom {
    display: none !important;
}
ol#posts li.notification {
    background: rgba(0, 0, 0, .6) !important;
    border-radius: 5px!important;
    border-bottom: 0px solid #000 !important;
}
#left_column #posts .notification a {
    color: #E0E0E0 !important;
    border-bottom: 0px !important;
}
div#right_column,
div#right_column:after {
    background: none !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/dashboard/iframe"), url-prefix("https://www.tumblr.com/dashboard/iframe") {
body {
    background: none!important;
}
}
