    font-size: 13px;
    display: block;
}
.ipsBreadcrumb.ipsBreadcrumb_bottom {
    border-top: 1px solid rgba(0,0,0,0.1);
    padding: 12px 0;
    /* margin: 25px 20px 0 20px; */
}
#ipsLayout_contentWrapper {
    width: 100%;
    background-color: rgba(11, 11, 11, 0.17);
}
.ipsApp .ipsButton_important {
    background: rgba(52, 55, 56, 0.59);
    color: rgba(255, 255, 255, 0.77);
}
#ipsLayout_header header {
    padding: 5px 0 130px 0;
    margin-bottom: -50px;
    background-image: url(http://i.imgur.com/OALDEUY.jpg);
    background-position: center;
    background-size: 1200px;
    background-repeat: no-repeat;
    background-color: #000000
}
.ipsNavBar_primary {
    position: relative;
    padding: 5px 5px 40px 5px;
    z-index: 2000;
    background: rgba(41, 41, 41, 0.42);
