    left: 0;
    right: 0;
    height: 40px;
    z-index: 1000;
    display: none;
    background: rgba(11, 11, 11, 0.44);
}
.ipsNavBar_secondary > li.ipsNavBar_active a {
	font-weight: bold;
    border-color: rgba(0,0,0,0.25);
    color: #CCC;
}
.ipsNavBar_secondary > li > a {
    color: rgba(236, 236, 236, 0.8);
    font-size: 13px;
    padding: 12px 18px 8px;
    border-bottom: 2px solid transparent;
}
.ipsBreadcrumb.ipsBreadcrumb_top {
    border-bottom: 1px solid rgba(0,0,0,0.075);
    margin: 0px 20px 15px 20px;
    padding: 12px 0;
    background: rgba(11, 11, 11, 0.39);
}
.ipsType_pageTitle {
    font-size: 26px;
    font-weight: 400;
    line-height: 1.2;
    margin: 0;
    color: #8B9CA5;
}
#elUserNav > li > a {
    color: rgba(255, 255, 255, 0.82);
    font-size: 13px;
    vertical-align: middle;
    line-height: 1;
}
.ipsBreadcrumb [data-action="defaultStream"], .ipsBreadcrumb [data-action="markSiteRead"] {
    font-size: 12px;
    font-weight: 400;
    color: #BBBBBB;
}
body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 13px;
    line-height: 18px;
    color: #A0A0A0;
    height: 100%;
    background-color: #000000;
}
a {
    color: #7E7F80;
    text-decoration: none;
}
a:hover {
    color: #929292;
}
.ipsApp .ipsButton_light {
    background: rgba(229, 229, 229, 0.48);
    border-color: rgba(0,0,0,0.03);
    color: #333333;
}
.ipsAreaBackground {
    background: rgba(11, 11, 11, 0.14);
}
