    color: #B4B4B4;
    font-size: 15px;
    cursor: text;
}
.cke_wysiwyg_frame, .cke_wysiwyg_div {
    background-color: #313131;
}
.ipsComposeArea_withPhoto .ipsComposeArea_editor {
    padding: 1px;
