    padding-right: 1px;
    padding-bottom: 1px;
    padding-left: 1px;
}
b, strong {
    font-weight: 400;
}
.cke_top {
    white-space: normal;
    background: rgba(33,33,33,1);
}
#ipsLayout_mainArea, #ipsLayout_sidebar {
    background: rgba(11, 11, 11, 0.28);
    display: table-cell;
    height: 100%;
    vertical-align: top;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -ms-box-sizing: border-box;
    -o-box-sizing: border-box;
    box-sizing: border-box;
}
.ipsType_richText {
    word-wrap: break-word;
    overflow-wrap: break-word;
    line-height: 1.5;
    color: #D0D0D0;
}
.ipsType_sectionHead {
    font-size: 20px;
    color: #DCDCDC;
    line-height: 24px;
    font-weight: 400;
    display: inline-block;
    margin: 0;
}
.ipsQuote .ipsQuote_citation a {
    color: #A1B3D6;
    text-decoration: none;
    font-weight: 400;
}
element.style {
    color: #839CFF;
}
.ipsAreaBackground_reset {
    background: rgba(35, 35, 35, 0.22);
}
.ipsBreadcrumb > ul > li {
    display: inline-block;
    line-height: 1;
    font-size: 13px;
    font-weight: 400;
    max-width: 500px;
    height: 15px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    vertical-align: bottom;
    color: #D2D2D2;
    padding-left: 15px;
}
.ipsDataItem_main {
    width: 100%;
    margin: 0;
    vertical-align: top;
}
html[dir="ltr"] .ipsDataItem_icon {
    padding-right: 0;
    background: rgba(19, 19, 19, 0);
}
.ipsMenu_headerBar {
    border-bottom: 1px solid #0B0B0B;
    box-shadow: 0px 1px 6px rgba(0,0,0,0.07);
    border-radius: 3px 3px 0px 0px;
    background: #1D1D1D;
    border-color: black;
}
.ipsMenu {
    background: rgba(35, 35, 35, 0.51);
    box-shadow: 0px 7px 20px rgba(0, 0, 0, 0.2);
    border: 1px solid #5D5D5D;
    border-radius: 3px;
    z-index: 10000;
    position: absolute;
}
.ipsDataItem {
    width: 100%;
    position: relative;
    border-width: 0 0 1px 0;
    border-style: solid;
    border-color: #0B0B0B;
    background-color: rgba(11, 11, 11, 0.33);
}
.ipsMenu_footerBar {
    border-top: 1px solid #5D5D5D;
    box-shadow: 0px -1px 6px rgba(0,0,0,0.07);
    border-radius: 0px 0px 3px 3px;
}
.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):not( .ipsDataItem_new ):not( .ipsDataItem_success ):not( .ipsDataItem_warning ):not( .ipsDataItem_error ):not( .ipsDataItem_info ):not( .ipsDataItem_status ):nth-child(even) {
    background: rgba(11, 11, 11, 0.28);
}
.ipsPad_half, .ipsApp ul.ipsPad_half, .ipsApp ol.ipsPad_half {
    padding: 1px;
}
.ipsWidget.ipsWidget_vertical .ipsWidget_title {
    padding: 10px;
    margin: 0;
    font-size: 14px;
    font-weight: 400;
    position: relative;
    color: #fff;
    background: #383838;
}
.ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    font-weight: 400;
    margin-bottom: 10px;
    background: #1D1D1D;
    padding: 10px;
}
.ipsList_inline, .ipsList_reset {
    margin: 0;
    padding: 8px;
    list-style: none;
}
.ipsTabs_panel {
    height: 100%;
    background: rgba(0, 0, 0, 0.46);
}
.ipsTabs {
    background: rgba(66, 69, 72, 0.38);
    padding: 10px 10px 0 10px;
    margin: 0;
    list-style: none;
}
.ipsTabs_activeItem {
    background: rgba(222, 219, 219, 0.42);
    color: #333;
    font-weight: bold;
}
