    box-shadow: 0px 5px 55px rgba(0,0,0,0.4);
    position: relative;
}
.ipsFieldRow_section {
    background: #383838;
    color: #929292;
    padding: 15px;
    font-size: 16px;
}
.ipsApp select {
    border: 1px solid #b3b3b3;
    border-radius: 3px;
    padding: 4px;
    background: rgb(56, 56, 56);
