css:
@-moz-document domain("omegle.com") {

body {

    background-color: #14151F !important;

    color: #74857C;

}

a:link {

    color: #FF0505 !important;

}

#header {

    background: none !important;

    border: 0px !important;

    box-shadow: 0 0em 0em #1A1B26;

    -moz-box-shadow: 0 0em 0em #1A1B26;

    -webkit-box-shadow: 0 0em 0em #1A1B26;

}

div#footer {

    display: none;

}

div#introtext a img {

    display: none;

}

#onlinecount {

    color: #14151F !important;

    top: 5px !important;

}

.logwrapper {

    background: #1A1B26 !important;

    border: none !important;

}

div.logwrapper[style],

div.controlwrapper[style] {

    margin-right: 0px!important;

}

.statuslog {

    color: #FFF!important;

}

.questionHeading {

    background: #14151F;

    color: #1A1B26;

}

.questionText {

    background: #FF3838;

}

.topictag {

    background: #000;

}

.topictageditor {

    background: rgb(107, 107, 107)!important;

    border: 1px solid #000!important;

}

img[src*="newchatbtn@2x.png"] {

    content: url("http://s33.postimg.org/ym6k7gbv3/New_Chat.png");

}

img[src*="textbtn@2x.png"] {

    content: url("http://s5.postimg.org/blvcgz6p3/Text_Btn.png");

}

img[src*="videobtn-disabled@2x.png"] {

    content: url("http://s19.postimg.org/lf2hb8pub/videobtn_disabled_2x.png");

}

img[src*="videobtn-enabled@2x.png"] {

    content: url("http://s19.postimg.org/fj8jnuaj7/videobtn_enabled_2x.png");

}

img[src*="exclamationog@2x.png"] {

    content: url("http://s19.postimg.org/68wfkaztb/exclamationog_2x.png");

}

img[src*="translogo@2x.png"] {

    content: url("http://s19.postimg.org/sm46740r7/translogo_2x.png");

}

img[src*="newchatbtn.png"] {

    content: url("http://s19.postimg.org/hg59sf177/New_Chat.png");

}

img[src*="textbtn.png"] {

    content: url("http://s5.postimg.org/blvcgz6p3/Text_Btn.png");

}

img[src*="videobtn-disabled.png"] {

    content: url("http://s19.postimg.org/lf2hb8pub/videobtn_disabled_2x.png");

}

img[src*="videobtn-enabled.png"] {

    content: url("http://s19.postimg.org/fj8jnuaj7/videobtn_enabled_2x.png");

}

img[src*="exclamationog.png"] {

    content: url("http://s19.postimg.org/68wfkaztb/exclamationog_2x.png");

}

img[src*="translogo.png"] {

    content: url("http://s19.postimg.org/sm46740r7/translogo_2x.png");

}

#logo img {

    display: none !important;

}

#tagline img {

    display: none !important;

}

#intro {

    border: 1px solid #000;

    background: rgb(20, 20, 20);

    left: 0px;

}

#startachat,

#intoheader {

    color: rgb(200, 200, 200);

}

#mobilesitenote {

    color: #1A1B26;

}

#monitoringnotice {

    border: 1px solid #000;

    box-shadow: 0 0 .75em #000000;

    -webkit-box-shadow: 0 0 .75em #000000;

    background: #1A1B26!important;

}

#monitoringnotice h2 {

    background: #1A1B26;

}

.youmsg .msgsource,

.strangermsg .msgsource {

    display: none;

}

.youmsg:before {

    content: "Y:";

    font-weight: bold;

}

.strangermsg:before {

    content: "S:";

    font-weight: bold;

}

.strangermsg {

    color: #FF5151;

}

.youmsg {

    color: #7485FF;

}

.disconnectbtn,

.sendbtn {

    background: #1A1B26 !important;

    color: #14151F !important;

}

.chatmsgwrapper,

textarea {

    background: #1A1B26 !important;

    color: #7485FF !important;

}

.disconnectbtnwrapper,

.sendbtnwrapper {

    border: none !important;

}

.chatmsgwrapper {

    border: none !important;

}

#onlinecount {

    font-size: 1em !important;

    color: #ccc !important;

}

#onlinecount strong {

    font-size: 1em !important;

    color: #ccc !important;

}

#sharebuttons {

    display: none !important;

}

.topictagtext,

.topictagdelete {

    color: #fff !important;

}

[target="_blank"] span {

    color: #777 !important;

    background: #1A1B26 !important;

}

.conversationgreat {

    background: #00459C;

}

}
