css:
@-moz-document domain("lolzteam.net") {
.bbCodeBlock .type {
    background-color: #151515;
    border-left: 0;
    color: #3f9889;
    padding: 6px 8px;
}
.bbCodeQuote .quoteContainer {
    background-color: #202020;
    border-left: 0;
    padding: 10px;
}
.discussionList .sectionHeaders {
    background-color: #141517;
    border-bottom: 3px solid #282828;
    border-top-width: 0;
}
.discussionListItem {
    background-color: #141517;
    border-bottom: 1px solid #282828;
}
.discussionListItem:nth-child(even) {
    background-color: #161819;
    border-bottom: 1px solid #282828;
}
.forum_list .nodeList .nodeList {
    border: none;
    margin-top: 0;
}
.forum_view .nodeList {
    border-top: 1px solid #535b63;
}
.message .likesSummary {
    background-color: #101112;
    border: 1px solid #2d2d2d;
    margin-top: 10px;
    padding: 5px;
}
.messageList .message {
    background-color: #141517;
    border: 1px solid #2d2d2d;
    margin-bottom: 10px;
    padding: 10px 10px 30px;
}
.messageText {
    font-family: Verdana, Arial, sans-serif;
    font-size: 13px;
}
.messageUserInfo {
    background-color: #2d2d2d;
    border: 0;
    display: table;
    margin-top: 5px;
}
.node .forumNodeInfo .nodeIcon.hasGlyph i:before {
    content: '\f1db';
}
.node .nodeIcon {
    margin: 10px 15px 10px 8px;
}
.node .nodeLastPost {
    background-color: #101112;
    border: 1px solid rgba(39, 42, 44, 0.37);
    padding: 10px;
}
.node .nodeLastPost .lastThreadUser,
.node .nodeLastPost .lastThreadDate {
    display: inline;
}
.node .nodeText {
    margin: 10px 270px 5px 56px;
    min-height: 0;
}
.node .subForumList {
    margin: 0;
}
.node .subForumList li .nodeTitle {
    background-color: inherit;
}
.node.level_2 .nodeInfo {
    border-top: inherit;
}
.nodeList .categoryStrip {
    background-color: #000;
    color: #ededed;
    padding: 9px 10px;
}
.nodeList .categoryStrip .categoryStripIcon,
.node .nodeLastPost .avatar,
.estsp_donation_form {
    display: none;
}
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo {
    background-color: #141517;
    border-bottom: 1px solid #282828;
    padding: 0;
}
.quickReply {
    background-color: #141517;
    margin-top: 3px;
}
.redactor_box {
    margin-top: 5px;
}
.secondaryContent {
    background-color: #141517;
}
.sectionFooter {
    background-color: #101112;
}
.visitorPanel .stats dl {
    background: #24242a;
}
body {
    background-color: #151515;
    font-family: 'Roboto Condensed', sans-serif;
    font-size: 12px;
}
#skins {
    background: #151515;
}
.messageUserInfo {
    background-color: #141517;
}
#replyButton {
    font-size: 12pt;
    font-weight: 700;
    float: right;
    text-decoration: none;
    text-align: center;
    width: 200px;
    line-height: 40px;
    height: 40px;
    margin: 15px 15px;
}
#beforestat {
    display: table-column-group;
    visibility: hidden;
}
element.style {
    color: rgb(40, 42, 44);
    margin: -15px 0;
}
.titleBar h1 {
    padding: 15px 10px 0;
    overflow: hidden;
    zoom: 1;
}
.messageList .messageUserInfo {
    border-right: 1px solid #141517;
}
.thread_view .threadAlerts {
    padding: 10px;
    z-index: 1;
    position: relative;
}
.message .likesSummary {
    margin-top: 4px;
    padding: 3px;
    text-shadow: #23a86d 0 0 10px;
    color: #2b8e8e;
}
.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background-image: url('http://lolzteam.net/styles/brivium/zinc/xenforo/node-sprite.png');
    background-repeat: no-repeat;
    background-position: -36px 0;
