css:
@-moz-document domain("epicgames.com") {
html {
background: #2a2a2a;
}

.above_body {
background: #3e3e3e
}
.navbar, .navtabs{
background: #606060;
  }

.navtabs li a.navtab {
background: none;
border-top: 1px solid #969696;
border-left: 1px solid #969696;
}

.navtabs li.selected a.navtab {
background: #ccc;
top: 0px;
padding-top: 0px;
}

/* Body */

body {
color: #000;
}

body a, #forums a.username {
color: #6699cc;
}


.body_wrapper {
background: #606060;      
}

.wgo_block .section, .wgo_block .blockbody, .navlinks, oolsmenu, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .forumhead + .childforum .L2:first-child .forumrow, .forumhead + .L2 .forumrow, .toolsmenu, .threadbit .nonsticky, .threadbit .discussionrow, .forum_info .blockbody, .blockbody, .thread_info .blockbody, .formcontrols .blockrow, #forumrules, .pmlist .blockrow, #usercp_nav .blockrow, #usercp_nav .blockbody, #sidebar_container.member_summary, li.userprof_content_border, .postbit .postbody, .polloptionsblock, .pollresultsblock   {
color: #fff;
background: #3e3e3e;
}

.threadbit .sticky, li.activitybit, #usercp_nav .blocksubhead, .postbit .userinfo, .postbit .userinfo_noavatar {
color: #fff;
background: #363636;
}

/* Borders */
.wgo_block .blockbody, .toolsmenu, .footer, #usercp_nav .blockbody, .userprof_headers_border, .userprof_content_border, #sidebar_container.member_summary, #sidebar_container .blockbody, li.userprof_content_border, #pollinfo, #pollresults {
border: 1px solid #2a2a2a;
}

.navlinks, .blocksubhead, #forumrules, .pmlist .blockrow {
border-top: 1px solid #2a2a2a;
}

.postbit .userinfo, .postbit .userinfo_noavatar, .postbit .posttitle, .postbitlegacy .title {
border-bottom: 1px solid #2a2a2a;
}

.pmbit, .visitormessage {
border-left: 1px solid #2a2a2a;
border-right: 1px solid #2a2a2a;
}

.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .forum_info .blockbody, .blockbody.settings_form_border, .thread_info .blockbody, .formcontrols, #forumrules {
border-bottom: 1px solid #2a2a2a;
border-left: 1px solid #2a2a2a;
border-right: 1px solid #2a2a2a;
}

.threadbit .nonsticky, .threadbit .deleted, .threadbit .discussionrow, .threadbit .ignored, .threadbit .sticky {
border: 1px solid #2a2a2a;
border-top: 0;
}

.threadbit .alt {
background: none;
}

/* title heads */
.forumhead, .wgo_block .blockhead, .threadlisthead, .forum_info .blockhead, .blockhead, .postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead, .thread_info h4, h2.searchlisthead, .newactivity, .moreactivity {
color: #fff;
background: #2a2a2a;
border: 1px solid #2a2a2a;
}

.blocksubhead, .blockfoot, .blocksubfoot {
color: #fff;
background: #2a2a2a;
}

.forumbit_nopost .subforumdescription {
background: #2a2a2a;
color: #999
}

.below_body {
background: #333;
}

.footer_time, .footer_copyright, .footer_morecopyright {
color: #999;
}

.below_body .time {
color: #dedede;
}
/* Search bar */
.globalsearch .textboxcontainer span {
border-left: 1px solid #969696;
border-top: 1px solid #969696;
}
.pagination span a.popupctrl, .pagination span a {
background: #2a2a2a;
}

.pagination span a, .navbar_search input {
border: 1px solid #3e3e3e;
}

.pagination span.selected a {
background: #606060;
border: 1px solid #3e3e3e;
}

#searchtypeswitcher {
border-bottom: 4px solid #404040;
}

#searchtypeswitcher li.selected a {
background: #404040;
}

/* forum posts */

.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
background: #3e3e3e;
border-left: 1px solid #2a2a2a;
}

.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow {
color: #ddd;
}

.postbit, .postbitlegacy, .eventbit {
color: #bbb;
}

.postbitlegacy .postdetails, .eventbit .eventdetails, .postbitlegacy .userinfo {
background: #4a4a4a;
}

.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols {
background: #454545;
}

.bbcode_container div.bbcode_quote, blockquote.preview .bbcode_container div.bbcode_quote, .attachments {
background: #606060;
border: 1px solid #bbb;
}
.signature {
border-top: 1px solid #2a2a2a;
}

.postbit, .postbitlegacy, .eventbit {
border: 1px solid #2a2a2a;
}

.bbcode_container div.bbcode_code, .bbcode_container pre.bbcode_code, .forumrow + .childsubforum .forumrow{
background: none;
}

/* User page */
dl.stats dt, #sidebar_container dl.stats, #sidebar_container .blockbody.userprof_content, #sidebar_container .userprof_content .time, .userprof_content .time {
color: #a6a6a6;
}

#sidebar_container .userprof_content, #sidebar_container .userprof_content .blockrow {
background: #606060;
border-color: #2a2a2a;
}

.forumrow + .childsubforum .forumrow, .forumhead + .childforum .L2:first-child .L2 .forumrow, .forumhead + .childforum .L2 .L2:first-child .forumrow {
background: #454545;
}

.time {
color: #fff
}

.toolsmenu .popupgroup a.popupctrl, .toolsmenu .popupgroup .popupmenu a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active, .lastedited {
color: #bbb;
}

}
