css:
@-moz-document domain("forums.heroesofnewerth.com") {

td img {
    border: 0;
    width: 0;
}


.body_wrapper {
    padding: 10px 20px 10px 20px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    margin: -153px auto auto;
    margin-top: -150px !important;

}

#pagetitle .description {
    font-size: 13.05px;
    opacity: 0;
}

#pagetitle h1 {
    font: bold 2em Tahoma,Calibri,Verdana,Geneva,sans-serif;
    font-size: medium;
}

#forums {
    width: 100%;
    margin-top: -50px;
}

#breadcrumb ul {
    text-shadow: 1px 1px 1px black;
    opacity: 1;
}

#headerHoN {
    margin-top: -30px;

}


#mLogo {
    background-image: url('http://i.imgur.com/ZCWgrfT.png') !important;
}


.btn-p4f {
    display: block;
    background-image: none !important;
    width: 147px;
    height: 28px;
    margin-left: 42px;
    margin-top: 3px;
    z-index: 90;
    cursor: pointer;
    overflow: hidden;
    position: absolute;
    border-radius: 4px;
    box-shadow: 0px 0px 50px -9px rgba(119, 199, 255, 0) !important;
    background-position: 0px 0px;
}
}
