@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("mega.nz"), domain("mega.co.nz"), domain("mega")

{



    html, body {

        background-color: #1A1B26 !important;

    }

    

    /* Tree */

    .fm-left-panel {

        background-color: #1A1B26 !important;

    }

    

    /* Tree > Cloud Drive */

    .fm-tree-header.cloud-drive-item, .fm-tree-header.cloud-drive-item.active.dragover, .fm-tree-header.cloud-drive-item.dragover,

    /* Tree > Folder */

    .fm-tree-folder,    

    /* Tree > Rubbish bin */

    .fm-tree-header.recycle-item.recycle-notification, .fm-tree-header.recycle-item.recycle-notification.active.dragover, .fm-tree-header.recycle-item.recycle-notification.dragover,

    /* Tree > Contacts */

    .fm-tree-header.contacts-item, .fm-tree-header.contacts-item.active.dragover, .fm-tree-header.contacts-item.dragover,

    /* Tree > Inbox */

    .fm-tree-header.messages-item, .fm-tree-header.messages-item.active.dragover, .fm-tree-header.messages-item.dragover

    {

        color: #1D1E2A !important;

    }

    

    /* Tree > Folder > Active Parents */

    .fm-tree-folder.lightactive {

        background-color: #F43D3D !important;

    }

    

    /* Tree > Folder > Active */

    .fm-tree-folder.active {

        background-color: #1A1B26 !important;

    }

    

    /* Tree > Folder > Hover */

    .fm-tree-folder:hover {

        background-color: #272727 !important;

    }

    

    /* Explorer background */

    .fmholder, #fmholder {

        background-color: #222 !important;

    }

    

    /* Explorer file sort bar border */

    .files-grid-view, .contacts-grid-view {

        border-top: 1px solid #333 !important;

    }

    

    /* Grid table header (i.e. List view file sort/description bar) */

    .grid-table-header th {

        border-left: 2px solid #303030;

        background-color: #222;

    }

    

    /* Unselected Files */

    .grid-table td, .grid-share-table td, .contacts-grid-table td {

        border-bottom: 2px solid #2c2c2c !important;

    }

    

    .transfer-table td, .grid-table td, .grid-share-table td, .contacts-grid-table td {

        background-color: #333 !important;

    }

    

    .grid-share-table td, .contacts-grid-table td {

        color: #eee !important;

    }



    /* Selected File */

    .grid-table tr.ui-selected td, .grid-share-table tr.ui-selected td, .contacts-grid-table tr.ui-selected td {

        background-color: #555 !important;

    }

    

    .transfer-table td, .grid-table td, .grid-share-table td, .contacts-grid-table td {

        color: #ddd !important;

    }

    

    /* Gridview Files */

    a, a:link, a:visited, a:hover, a:active {

        color: #ddd !important;

    }

    

    .file-block, .contact-block-view {

        border: 2px solid #444 !important;

        color: #ddd !important;

    }

    

    .file-block:hover, .contact-block-view:hover {

        background-color: #444 !important;

    }

    

    /* Explorer directory path bar */

    .fm-back-button,

    .fm-breadcrumbs-block, .fm-share-breadcrumbs-block,

    .fm-breadcrumbs.contains-directories.has-next-button .right-arrow-bg, .fm-share-breadcrumbs.contains-directories.has-next-button .right-arrow-bg {

        background: #333 !important;

    }

    

    /* New folder, Upload file */

    .fm-new-folder, .search-files-result,

    .fm-file-upload, .search-messages-result {

        border-color: #222 !important;

        background-color: #333 !important;

    }

    

    /* Selected file options bar */

    .top-context-menu {

        background-color: #333 !important;

    }

    

    .top-icons-block .context-menu-item {

        background-color: #333 !important;

    }



    .top-context-border {

        border: 2px solid #333 !important;

    }

    

    /* Dropdowns */

    .context-menu {

        background-color: #444 !important;

    }

    

    /* Tasks context menu */

    .fm-new-folder, .search-files-result,

    .fm-file-upload, .search-messages-result {

        color: #eee !important;

    }

    

    /* Transfer Panel */

    .transfer-panel {

        background: #333 !important;

    }

    

    .transfer-panel-title {

        color: #aaa !important;

    }

    



    /* Suggestion by @megadarkork */

    .new-download-file-title, .new-download-file-size {

        color: #ddd !important;

    }

    

    

    /*

    FULL TABLE LIST

    .transfer-table th, .transfer-table td, .transfer-table-header th, .grid-table th, .grid-table td, .grid-share-table th, .grid-share-table td, .grid-table-header th, .contacts-grid-header th, .contacts-grid-table th, .contacts-grid-table td

    */



}





/* Additional styling for mega.nz */

@-moz-document domain("mega.nz"), domain("mega")

{

    

    /* Tree header */

    .nw-conversations-header, .nw-tree-panel-header, .copy-dialog-panel-header, .move-dialog-panel-header, .share-dialog-panel-header {

        background-color: #222 !important;

    }

    

    /* Explorer directory path bar */

    .fm-right-header, .new-notification-top {

        background-color: #333 !important;

    }

    

    .fm-breadcrumbs .right-arrow-bg, .fm-share-breadcrumbs .right-arrow-bg {

        color: #888 !important;

    }



    /* Tree > Folder */

    .nw-fm-tree-folder {

        color: #ddd !important;

    }

    

    /* Listview */

    .tranfer-filetype-txt, .fm-browsers-txt, .fm-flags-txt, .fm-member-icon-txt {

        color: #ddd !important;

    }

    

    /* Gridview Icons */

    .file-icon-area {

        background-color: #222 !important;

    }

    

    .file-block-title, .contact-block-view-name {

        color: #ccc !important;

    }

    

}
