css:
@-moz-document domain("gitter.im") {
.desktop .room-menu__panel {
	width: 21rem;
}

.desktop .room-menu__panel.active {
	width: 26rem;	
}

.chat-item__container {
	margin: .4em 0 0;
	padding-left: 0;
}

.desktop .room-menu-container {
	width: 4.5rem;
}

.room-menu-options__item--all,
.room-menu-options__item--search,
.room-menu-options__item--people,
.room-menu-options__item--close,
.room-menu-options__item-button,
.room-menu-options__item--org {
	min-width: 4.5rem;
	width: 4.5rem
}

.room-menu-options__item--org,
.desktop .room-menu-options__item--org,
.desktop .room-menu-options__item--people,
.desktop .room-menu-options__item--search,
.desktop .room-menu-options__item--all,
.desktop .room-menu-options__item--close {
	min-height: 40px;	
	height: 40px;
	line-height: 40px;
}

.room-menu-options__item--search {
	top: 40px;
}

.room-menu-options__item--people {
	top: 80px;
}

.minibar-inner {
	top: 120px;
}

.room-menu-options__item__avatar,
.menu-toggle-icon {
	width: 1.6rem;
	height: 1.6rem;
}

.js-create-room-button {
	margin-bottom: 6px;
}

.panel-footer__create-room {
	width: auto;
}

.panel-header__title--all,
.panel-footer__title {
	font-size: 1.9rem;
}

.chat-input__container {
	margin-left: 0;
}

.room-item__title {
	font-weight: normal;	
}
}
