css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hornetapp.com") {
  div#edit-profile-left, div#filters {
    transition-property: margin-left;
    transition-duration:0.2s;
    transition-timing-function: ease-in-out;
    position:absolute;
    z-index:100;
    background-color: white; 
    margin-left: calc( -20% + 40px );
  }
  div#edit-profile-left:hover, div#filters:hover{
    margin-left: 0;
  }
  div#grid {
    margin-left:50px;
  }
  div#grid ul li{
    transition-property: transform;
    transition-duration:0.2s;
    transition-timing-function: ease-in-out;
  }
  div#grid ul li:hover{
    transform: scale(1.5);
    z-index:100;
  }
  div#grid ul li img{
    transition-property: border-radius;
    transition-duration:0.2s;
    transition-timing-function: ease-in-out;
    border-radius:5px;
  }
  div#grid ul li:hover img, div#grid ul li:hover{
    border-radius:50%;
  }
  body div#grid ul li:nth-child(1) {
    width:0;
    padding-bottom:0;
    visibility:hidden;
  }
  body div#grid ul li:nth-child(n+2) {
    padding-bottom: 31%;
    width: 31%;
  }
  body div#grid ul li:nth-child(n+5) {
    padding-bottom: 24%;
    width: 24%;
  }
  body div#grid ul li:nth-child(n+13) {
    padding-bottom: 15%;
    width: 15%;
  }
  body[par='7'] div#grid ul li {
    padding-bottom: 13%;
    width: 13%;
  }
  body[par='6'] div#grid ul li {
    padding-bottom: 15%;
    width: 15%;
  }
  body[par='5'] div#grid ul li {
    padding-bottom: 19%;
    width: 19%;
  }
  body[par='4'] div#grid ul li {
    padding-bottom: 24%;
    width: 24%;
  }
  body[par='3'] div#grid ul li {
    padding-bottom: 33%;
    width: 33%;
  }
  div#messenger{
    width:calc( 40% - 50px );
    margin-left:calc( 60% + 60px );
  }
  div#messenger div#active-conversation{
    right: calc( 40% - 60px );
    width: 450px;
  }
  div#messenger div#active-conversation div.messages {
    transition-property: height;
    transition-duration:0.2s;
    transition-timing-function: ease-in-out;
    height:15em;
  }
  div#messenger:hover div#active-conversation div.messages {
    height:45em;
  }
  div#messenger div#conversations div.conversation {
    transition-property: height, font-size;
    transition-duration:0.2s;
    transition-timing-function: ease-in-out;
    height: 3.5em;
  }
  div#messenger div#conversations div.conversation:hover {
    font-size:1.5em;
    height: 5em;
    padding-top:0.5em;
  }
  div#messenger div#conversations div.conversation div.thumbnail{
    transition-property: padding-top;
    transition-duration:0.2s;
    transition-timing-function: ease-in-out;
  }
  div#messenger div#conversations div.conversation:hover div.thumbnail {
    transform:scale(1.3);
    padding-top:0.5em;
  }
  div#messenger div#conversations div.conversation:hover div.status-icon {
    transform:scale(1.3);
  }
  div#member-profile {
    padding-left:1.8em;
    padding-right:1.8em;
    /*background-color:#fb5827;
    background-color:rgba(251,88,29,0.5);*/
    background-color:rgba(50,50,50,0.8);
    border-radius:0.5em;
    height:auto;
    border:3px solid #fb5827;
  }
  div#member-profile ~ div.overlay {
    width:0;
  }
  div#member-profile {
    top:calc( 48px +  44px );
    margin-right:calc( 40% - 60px );
  }
  div#member-profile div#gallery div#gallery-inner {
    overflow: visible;
  }
  div#member-profile div#gallery div#gallery-inner div.thumbnail {
    transition-property: transform, border-radius;
    transition-duration:0.2s;
    transition-timing-function: ease-in-out;
    border-radius:5%;
    z-index:15000;
    overflow:visible;
  }
  div#member-profile div#gallery div#gallery-inner div.thumbnail:hover {
    transform: scale(2);
    border-radius:50%;
  }
}
