css:
@-moz-document domain("www.slackware.com") {
/* version 0.2 */
body {
    background: #eaebec;
}

body > center > table {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
}

td {
    background: #f1f1f1;
}

a {
    color: #1f70a8;
    text-decoration: none;
    font-weight: bold;
    transition: border .8s;
    border-bottom: 1px solid transparent;
}
a:hover {
    border-color: #1f70a8;
    text-decoration: none;
}

/*
Header
-----------------
body > center > table > tbody > tr:first-child
*/
body > center > table > tbody > tr:first-child td {
    background: white;
    color: #2a3037;
}
body > center > table > tbody > tr:first-child > td:first-child {
    border-bottom: 1px solid #d8dadd;
}
body > center > table > tbody > tr:first-child td:first-child {
    font-size: 2em;
}


/*
Navigation
-----------------
body > center > table > tbody > tr:nth-child(2) > td:first-child
*/
body > center > table > tbody > tr:nth-child(2) > td:first-child {
    background: #2a3037 !important;
    width: 16%;
}

body > center > table > tbody > tr:nth-child(2) > td:first-child * {
    background: #2a3037 !important;
    color: white;
}

body > center > table > tbody > tr:nth-child(2) > td:first-child p {
    margin: 0;
}
body > center > table > tbody > tr:nth-child(2) > td:first-child a {
    text-decoration: none;
    color: #c5d0de;
    line-height: 2em;
    font-size: 1.1em;
    border-bottom: none;
    transition: color .3s;
}
body > center > table > tbody > tr:nth-child(2) > td:first-child a:before {
    content: ' ☆ ';
}
body > center > table > tbody > tr:nth-child(2) > td:first-child a:hover {
    color: white;
    width: 100%;
    display: inline-block;
}
body > center > table > tbody > tr:nth-child(2) > td:first-child a:hover:before {
    content: ' ★ ';
}

/*
Content
-----------------
body > center > table > tbody > tr:nth-child(2) > td:nth-child(2)
*/

/* Remove spacing */
body > center > table > tbody > tr:nth-child(3) {
    display: none;
}

/* Copyright notice / footer */
body > center > table > tbody > tr:nth-child(4) > td:first-child{
    box-shadow: inset 0 2px 2px 0 rgba(0,0,0,0.16),0 0 2px 0 rgba(0,0,0,0.12);
    padding: 1em;
}
body > center > table > tbody > tr:nth-child(4) td {
    background: #1f70a8;
    color: white;
    
}
body > center > table > tbody > tr:nth-child(4) a {
    color: white;
}
body > center > table > tbody > tr:nth-child(4) font {
    font-size: 1em !important;
}
}
