css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(helgon.se), domain(helgon.net) {
    body

{
    background-image: url("http://orig03.deviantart.net/a483/f/2013/277/d/c/kill_la_kill_1_by_ror362-d6p8ju8.png") !important;
    background-repeat: no-repeat;
    background-color: rgb(53,53,53);
    background-position: left;
    background-attachment: fixed;
    background-size: contain;
}
    #cbox
    {
        visibility: visible!important;
        background: rgb(46, 15, 31);
        border-radius:8px;
    }
    .wrapper_ext
    {
        max-width:1200px;
    }
    #col_banner
    {
        visibility:hidden!important;
display:none!important;
    }
    #wrapper_sidebar
    {
        margin:0 auto;
    }

    *
    {
         font-family: helvetica,sans-serif!important;
    }
    
table {
    background-image: none!important;
}

div#log_top {
    background-image: none !important;
    background-color: rgb(46, 15, 31);
}

div#log_top_logo {
    background-image: url("https://upload.wikimedia.org/wikipedia/commons/thumb/e/ed/Kill_la_Kill_logo.svg/2749px-Kill_la_Kill_logo.svg.png")!important;
    background-size: 147px 71px;
}

div#top_logo img {
    opacity: 0;
}

div#top_logo {
    background-image: url("https://upload.wikimedia.org/wikipedia/commons/thumb/e/ed/Kill_la_Kill_logo.svg/2749px-Kill_la_Kill_logo.svg.png")!important;
    background-size: 147px 71px;
}

div#log_top_logo img {
    opacity: 0;
}

div#log_bottom {
    background-image: none!important;
    background-color: rgb(46, 15, 31);
}

/*kroppen vid inloggning*/
div#log_body {
    background-image: url("https://media.giphy.com/media/diehB2UhSBwic/giphy.gif")!important;
    background-color: rgb(46, 15, 31);
    background-size: cover;
    height: 360px;
}

div#log_body_left {
    visibility: hidden;
}

div#log_body_right {
    visibility: hidden;
}

    /*färg på rutan med innehåll (typ presentation)*/
.b_b {
    background-color: hsla(0, 0%, 5%, 1);
    font-family: helvetica,sans-serif!important;
}

.b_t {
    background-image: none!important;
    background: hsla(0, 0%, 4%, 1)!important;
    
}

.b_tr {
    background: none!important;
}
    
.b_tr a:link
        {
            color: rgba(255, 224, 102, 1)!important; 
        }
       

.b_t a:hover {
      color: rgba(255, 224, 102, 1)!important;
}
    
    
    /*FÄRGEN PÅ HUVUDELEN AV KROPPEN*/
div#main {
    background-image: none!important;
    background-color: rgb(46, 15, 31);
    border: 2px solid;
    border-radius: 10px;
    border-color: rgb(70,46,50);
    margin-top:0;
}

    /*bannern på toppen av sidan*/
div#top {
    background: url("https://4.bp.blogspot.com/-m7F5dFziKNQ/VExSTJ3anhI/AAAAAAAAKaY/n6lyp06TGqg/s1600/kill_la_kill.png")!important;
    background-color: red!important;
    background-size: auto !important;
    height: 79px;
    width: inherit;
    
}

    /*översta bannern med länkar*/
div#mmt {
    background-image: none!important;
    /* background-color:#FFCC00;*/
    background: -moz-radial-gradient(center, ellipse cover, rgba(236,4,15,1) 0%, rgba(196,3,13,1) 44%, rgba(148,0,7,1) 100%)!important; /* FF3.6+ */
}

    div#mmt .center a, div#smt .center a, div#content .b_t a {
        color: hsl(57,33%,93%)!important;
        font-weight: bold!important;
    }

        div#mmt .center a:hover, div#smt .center a:hover, .ttt a:hover {
            transition: color 0.5s;
            color: rgb(255, 207, 15)!important;
            text-shadow: 2px 2px hsla(0,20%,0%,0.2);
        }
/*bannern under översta bannern*/
#smt {
    background: none!important;
    background: rgba(132, 6, 6, 1)!important; /* FF3.6+ */
    
    height:20px !important;
    min-width:1000px;
    overflow:HIDDEN;
}

    #smt .center
    {
        min-width:800px;
    }
    
/* färg inkl gradiens på texten i den översta länkatalogen*/
div#mmt a {
    border-right: 1px solid hsla(0,20%,0%,0.1);
    /*border-radius: 8px;*/
    box-shadow: 1px 1px 2px hsla(0,20%,0%,0.2);
    background:  radial-gradient(ellipse at center, rgba(252,54,64,1) 0%, rgba(251,4,16,1) 44%, rgba(189,0,9,1) 100%); 


}

    div#mmt a:active {
        border: 1px solid hsla(0,20%,0%,0.2);
       /*border-radius: 8px;*/
        box-shadow: 2px 2px 2px hsla(0,20%,0%,0.2);
        background-color: hsla(0,10%,0%,0.1);
        text-shadow:1px 1px hsla(0,40%,0%,0.5)!important;
    }
    /* färg inkl gradiens på texten i länkatalogen under översta bannern */
    div#smt a {
    border-right: 0px solid hsla(0,20%,0%,0.2);
    /*border-radius: 5px;*/
    box-shadow: 1px 1px 2px hsla(0,20%,0%,0.2);
    padding-left:10px;
    padding-right:10px;
   /* background: radial-gradient(ellipse at center, rgba(121,191,51,0.5) 23%, rgba(121,191,51,0.5) 24%, rgba(145,232,66,0.2) 75%);
        är till för färg (gradiens) om man känner fört*/

        
}

    div#smt a:active {
        border: 1px solid hsla(0,40%,20%,0.5);
        border-radius: 1px;
        box-shadow: 2px 2px 2px hsla(0,20%,0%,0.2);
        background-color: hsla(0,10%,0%,0.1);
        text-shadow:1px 1px hsla(0,40%,0%,0.5)!important;
    }

div#mmt .center {
    /*width: 730px!important;
    height:31px!important;*/
    width:auto!important;
    float:none!important;
 
}
    div#mmt .center ul {
    /*width: 730px!important;
    height:31px!important;*/
    margin:auto;
}
    div#smt .center {
}
