       background:url(https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSVzTL_OA9vS2HntKcQcyFFqa8Mq7t0VVxAlB6Ma1KuARhd3XSW);
      background-size: 1240px 20px;
    }*/

div#smb {
    visibility: hidden;
}

.ttt .o {
    color: green!important;
}

.ttt .o:hover {
    transition: color 0.5s!important;
    color: hsl(90,93%,60%)!important;
    text-shadow: 2px 2px hsla(0,20%,0%,0.2);
}
a:hover {
    background-image: none!important;
    text-decoration: none!important;
    color: rgb(102,178,255);
}

a {
    color: hsl(68,61%,84%);
    transition: color 0.5s;
}
    .p_l a{
        color:purple;
    }

hr {
    border: 1px dotted #303030!important;
}

#sidebar .center select,#sidebar .center input {
    background-color: rgb(102,178,255) !important;
}
    div#col_side .button #sidebar .center {
        background: -moz-linear-gradient(top, rgba(121,191,51,1) 0%, rgba(76,151,35,1) 50%, rgba(45,139,1,1) 51%, rgba(85,166,38,1) 100%)!important;
    }
    
    #sidebar
    {
        width:100%;
        height:20px;
        transition: height 1s;
        overflow:hidden;
        border:none;
    }
    #sidebar:hover
    {
        height:445px;
       
        
    }
    

div#content .b_tl .o {
    color: hsl(112,99%,17%);
    text-shadow: 1px 1px hsla(112,99%,57%,0.1);
    transition:color 0.2s;
}
    div#content .b_tl .o:hover {
        transition:color 0.2s;
    color: hsl(112,99%,27%);
    text-shadow: 2px 2px hsla(112,99%,57%,0.3);
}

div#mmt a:hover {
    text-decoration: none;
    color: rgb(102,178,255)!important;
}

div#smt a:hover {
    text-decoration: none;
    color: rgb(102,178,255)!important;
}
    div#col_side .b_w .b_b .ttt a:not(.o){
color:red !important;        
        
    }
    div#col_side .b_w .b_b .ttt a:not(.o):hover{
       color: hsl(0,93%,60%)!important;
       transition: color 0.5s!important;
       text-shadow: 2px 2px hsla(0,20%,0%,0.2);
    }
}
@-moz-document url-prefix(http://beta.helgon.se/start/start.aspx){
    #col_side .b_w:nth-child(2)
    {
    visibility: hidden!important;
        display:none!important;
    }
}
