css:
@-moz-document domain("socialani.me") {
li.thumb-series div.thumb-tittle {
    background-color: rgba(0, 0, 0, 0.7);
    bottom: 25px;
    color: #FFF;
    margin: 0;
    padding: 10px 0;
    position: absolute;
    text-align: center;
    width: 100%;
    text-shadow: 1px 1px 0 #000;
    pointer-events: none;
}

.thumb-picture:hover {
    color: #FF7400;
}

ul.anime-content li.thumb-series div.thumb-picture {
    border-bottom: 5px solid #222;
    border-radius: 3px 3px 3px 3px;
    border-right: 5px solid #222;
    border-top: 5px solid #222;
}

ul.anime-content li.thumb-series div.thumb-picture:hover {
    border-bottom: 5px solid #FF7400;
    border-radius: 3px 3px 3px 3px;
    border-right: 5px solid #FF7400;
    border-top: 5px solid #FF7400;
}

div.thumb-status {
    z-index: 20;
    right: 1px;
    top: 5px;
}

a.changeStatusSeries{
	z-index: 21;
}

.thumb-picture:before {
    content: '';
    background: url("http://i.imgur.com/GCY03Lw.png") no-repeat scroll 0 0 transparent;
    box-shadow: 2px 0 1px rgba(0, 0, 0, 0.2) inset;
    height: 100%;
    background-size: 100% 100%;
    width: 100%;
    /* left: 0; */
    position: absolute;
    /* top: 5px; */
    /* width: 180px; */
    z-index: 15!important;
}

.thumb-picture:hover::before {
    content: '';
    background: url("http://i.imgur.com/IpOA4VG.png") no-repeat scroll 0 0 transparent;
    box-shadow: 2px 0 1px rgba(0, 0, 0, 0.2) inset;
    height: 100%;
    background-size: 100% 100%;
    width: 100%;
    /* left: 0; */
    position: absolute;
    /* top: 5px; */
    /* width: 180px; */
    z-index: 15!important;
}

div.thumb-status ul:before {
    z-index: 50!important;
}

div.thumb-status ul:after {
    z-index: 50!important;
}
div.thumb-status ul li a {
    padding: 9px 10px;
}

}

@-moz-document url-prefix("http://socialani.me/files") {
div.box-left div.img {
	padding: 0px; 
    border-bottom: 5px solid #222;
    border-radius: 3px 3px 3px 3px;
    border-right: 5px solid #222;
    border-top: 5px solid #222;
}

div.box-left div.img:hover {
    border-bottom: 5px solid #FF7400;
    border-radius: 3px 3px 3px 3px;
    border-right: 5px solid #FF7400;
    border-top: 5px solid #FF7400;
}

div.box-left div.img:before {
    content: '';
    background: url("http://i.imgur.com/GCY03Lw.png") no-repeat scroll 0 0 transparent;
    box-shadow: 2px 0 1px rgba(0, 0, 0, 0.2) inset;
    height: 100%;
    background-size: 100% 100%;
    width: 100%;
    /* left: 0; */
    position: absolute;
    /* top: 5px; */
    /* width: 180px; */
    z-index: 15!important;
}

div.box-left div.img:hover::before {
    content: '';
    background: url("http://i.imgur.com/IpOA4VG.png") no-repeat scroll 0 0 transparent;
    box-shadow: 2px 0 1px rgba(0, 0, 0, 0.2) inset;
    height: 100%;
    background-size: 100% 100%;
    width: 100%;
    /* left: 0; */
    position: absolute;
    /* top: 5px; */
    /* width: 180px; */
    z-index: 15!important;
}
}
