css:
@-moz-document domain("wattpad.com") {
body {
    background-color: #252525 !important;
}

#search-query {
    background-color: #252525 !important;
}

#content_wrapper {
    background-color: #252525 !important;
}

#edit-controls {
    background-color: #252525 !important;
    border-bottom: 2px solid #ff9400 !important;
}

main {
    background-color: #252525 !important;
}

.container {
    background: transparent !important;
    background-color: #252525 !important;
    border: none !important;
    color: #c8c8c8 !important;
}

#creation-works-new {
    background: transparent !important;
    background-color: #252525 !important;
}

.on-nav-item {
	color: #c8c8c8 !important;
}

.container.left {
    background-color: #252525 !important;
    border-radius: 8px !important;
    border: 1px solid #ff9400 !important;
}

.container.left > div > div {
    color: #c8c8c8 !important;
}

.container_head {
    border-bottom: 1px solid #ff9400 !important;
    color: #c8c8c8 !important;
}

.commentby {
    background: transparent !important;
}

.commentbubble {
    padding: 2px !important;
    border-top: 1px solid #ff9400 !important;
    border-left: 1px solid #ff9400 !important;
    background-color: #252525 !important;
    padding-left: 5px !important;
    padding-top: 3px !important;
    color: #c8c8c8 !important;
}

.commentbubble>textarea {
    background-color: #303030 !important;
    border: 1px solid #ff9400 !important;
    border-top: none !important;
    border-radius: 5px !important;
    color: #c8c8c8 !important;
}

#commentbutton {
    border-radius: 3px !important;
    border: 1px solid #ff9400 !important;
}

.commentbubble reverse {
    background-color: #252525 !important;
}

.container.left>div {
    border: none !important;
}

.commentbubble.reverse {
    padding-left: 5px !important;
    padding-top: 3px !important;
    border: none !important;
    border-bottom: 1px solid #ff9400 !important;
    border-right: 1px solid #ff9400 !important;
    color: #c8c8c8 !important;
}

.commentauthor {
    border-bottom: 1px solid #ff9400 !important;
    border-right: 1px solid #ff9400 !important;
}

#footer-container {
    background-color: #252525 !important;
}

#app-container {
    background-color: #252525 !important;
}

.collection {
    background-color: #252525 !important;
}

.module-title {
    color: #c8c8c8 !important;
}

.title {
    color: #c8c8c8 !important;
}

.dropdown-menu {
    background-color: #252525 !important;
    border: 2px solid #ff9400 !important;
    border-radius: 4px !important;
}

.dropdown-menu > ul > li > a {
    color: #c8c8c8 !important;
    transition: 0.5s !important;
}

.dropdown-menu > ul > li > a:hover {
    color: #ff9400 !important;
    transition: 0.5s !important;
}

.dropdown-menu > div > ul > li > a {
    color: #c8c8c8 !important;
    transition: 0.5s !important;
}

.dropdown-menu > div > ul > li > a:hover {
    color: #ff9400 !important;
    transition: 0.5s !important;
}

.bold > li > a {
    color: #c8c8c8 !important;
    transition: 0.5s !important;
}

.bold > li > a:hover {
    color: #ff9400 !important;
    transition: 0.5s !important;
}

.panel {
    background-color: #252525 !important;
    border: none !important; /*2px solid #ff9400 !important; */
	border-radius: 3px !important;
    padding-left: 5px !important;
}

button.active {
    border-bottom: 5px solid #ff9400 !important;
}

.on-switch-type {
    color: #c8c8c8 !important;
}

#publish-count-wrapper {
    color: #c8c8c8 !important;
}

.story-description {
    background-color: #303030 !important;
    color: #c8c8c8 !important;
    border: 1px solid #ff9400 !important;
}

.category > label {
    color: #c8c8c8 !important;
}

.language > label {
    color: #c8c8c8 !important;
}

.copyright > label {
    color: #c8c8c8 !important;
}

.human-rating > label {
    color: #c8c8c8 !important;
}

select {
    background-color: #303030 !important;
    color: #c8c8c8 !important;
    border: 1px solid #ff9400 !important;
}

.rating-message {
    color: #aa0000 !important;
}

#writer-navigation {
    background-color: #252525 !important;
    border-bottom: 2px solid #ff9400 !important;
    border-radius: 3px;
}

.info {
    color: #c8c8c8 !important;
}

.background-lg {
    background-color: #252525 !important;
    border-bottom: 2px solid #ff9400 !important;
    border-radius: 3px;
}

#writer-editor {
    background-color: #252525 !important;
}

#story-title {
    color: #c8c8c8 !important;
}

.story-title > .on-navigate {
    color: #c8c8c8 !important;
    transition: 0.5s !important;
}

.story-title > .on-navigate:hover {
    color: #ff9400 !important;
    transition: 0.5s !important;
}

.story-editor {
    background-color: #303030 !important;
    color: #c8c8c8 !important;
    border: 1px solid #ff9400 !important;
}

#writer-editor-container {
    background-color: #252525 !important;
}

#funbar {
    background-color: #252525 !important;
}

.story-part {
    background-color: #252525 !important;
}

.new-post {
    background-color: #252525 !important;
}

.message > textarea {
    background-color: #303030 !important;
    color: #c8c8c8 !important;
}

.message > .triangle {
    display: none !important;
}

.panel-reading > pre > p {
    color: #c8c8c8 !important;
}

.panel-reading > h2 {
    color: #c8c8c8 !important;
}

.discover-rows {
    background-color: #252525 !important;
}

.modal-body {
    background-color: #252525 !important;
    border: 0px solid #ff9400 !important;
    border-radius: 3px !important;
}

.content {
    background-color: #252525 !important;
}

.modal-title {
    color: #c8c8c8 !important;
    transition: 0.5s !important;
}

.modal-title:hover {
    color: #ff9400 !important;
    transition: 0.5s !important;
}

.description {
    color: #c8c8c8 !important;
}

.description > pre {
    color: #c8c8c8 !important;
}

#progressbar {
    background-color: #ff9400 !important;
    background: linear-gradient(to right, #bb5000, #ff9400) !important;
}

td {
    color: #c8c8c8 !important;
}

.inboxmeta {
    color: #c8c8c8 !important;
}

.sub-navigation {
    background-color: #252525 !important;
}

.panel-footer {
    background-color: #252525 !important;
}

.panel-heading {
    background-color: #252525 !important;
}

.panel-title > .on-navigate {
    color: #c8c8c8 !important;
    transition: 0.5s !important;
}

.panel-title > .on-navigate:hover {
    color: #ff9400 !important;
    transition: 0.5s !important;
}

.fa {
    display: none !important;
}

.alt-link {
    color: #c8c8c8 !important;
}

.pinned-item > .username {
    color: #c8c8c8 !important;
    transition: 0.5s !important;
}

.pinned-item > .username:hover {
    color: #ff9400 !important;
    transition: 0.5s !important;
}

.pinned-item > .body {
    color: #c8c8c8 !important;
}

.story.small-width > strong > .on-navigate {
    color: #c8c8c8 !important;
    transition: 0.5s !important;
}

.story.small-width > strong > .on-navigate:hover {
    color: #ff9400 !important;
    transition: 0.5s !important;
}
#de {
    color: #c8c8c8 !important;
}
}
