@-moz-document domain("hattrick.org") {

/***** GENERAL *****/
body {
	background-image: none !important;
	background-color: #282828 !important;
	/*color: teal !important;*/
}
#content {
	background-color: #DCE2DC!important;
	border:none !important;
	background-image: none !important;
}
a {
	text-decoration:none !important;
}
a:hover {
/*	color:red !important;*/
}

/****** HEADER ******/
#menu {
	height: 25px !important;
	margin-top: 2px !important;
}
#menu a {
	line-height: 25px !important;
}
#header {
	height: 96px !important;
	padding-top: 5px !important;
}
#teamLinks, #time, #online{
	margin-top: 4px !important;
}

/***** FOOTER *****/
#footer {
	display: none !important;
}

/***** HT-LIVE *****/
.main .liveHighlight {
	color: red !important;
}


/**** NOTEBOOK ****/
div.notebook {
	background-image:none !important;
	background-color:#FFFDE1 !important;
}
textarea.notebook {
	border:solid 1px #282828 !important;
	padding: 5px !important;
	width: 515px !important;
	height: 450px !important;
}

/**** CONFERENCES ****/
.forumTabs a.shortcuts, .forumTabs a.all, .forumTabs a.new {
	padding-right:2px !important;
	padding-left:2px !important;
}
img.confDown, img.confRight {
	background-position: 0 1px !important;
}
.cfHeader br { 
	display: none !important;
}
.doubleLine {
	height: 16px !important;
}

.cfHeader img[src*="/Img/Icons/transparent.gif"] {
	margin-right:3px !important;
}

