css:
@-moz-document url-prefix("http://www.shamusyoung.com/twentysidedtale/") {
body {
    background: none!important;
    background-color: #000!important;
    color: #ff007f;
}

h1 {
    color: #6969c8;
}

h2 {
    color: #6969c8;
}

h3 {
    color: #6969c8;
}

h4 {
    color: #6969c8;
}

h5 {
    color: #6969c8;
}

h6 {
    color: #6969c8;
}

div {
    color: #5d5d5d;
}

#respond {
    color: white;
}

.snote_refnum {
	color: #6565eb;
    font-size: .8em;
    text-decoration: none;
}    

.entry-text {
    font-family: 'Roboto', sans-serif;
    font-size: large;
    color: #ebebeb;
    clear: both;
    padding: 30px;
    background: #1e1e1e;
}
.splash-title {
    font-size: 30px;
    text-shadow: 2px 2px 6px #666;
    background: #101010;
    color: #ebebeb;
    padding: 3px;
}
.splash-title.a:link {
    color: white;
}
.splashbox {
    font-size: 18px;
    width: 800px;
    background: #101010;
    color: #ebebeb;
    padding: 5px;
}
acronym {
    color: #ff00fc;
    border-bottom: 2px dotted #557;
}
blockquote {
    margin: 0 20px;
}
a,
a:link {
    color: #6565eb;
    text-decoration: none;
    font-weight: bold;
}
a:visited {
    color: #6969c8;
    text-decoration: none;
}
a:hover {
    color: #7c7cee;
    text-decoration: none;
}
a:active {
    color: #7c7cee;
    text-decoration: none;
}
.entry {
    background: #101010;
}
.prev-next-table {
    background: #101010;
    margin: 0px;
    padding: 0px;
}
.prev-next-arrow {
    background: #1e1e1e;
    height: 33px;
    color: white;
    padding: 3px;
    margin: 0px;
    font-size: 30px;
}
.prev-next-arrow a {
    vertical-align: text-top;
    color: white;
}
.prev-next {
    font-size: 15px;
    height: 34px;
    overflow: hidden;
    border-right: 1px solid #bbb;
    border-left: 1px solid #bbb;
    background: #1e1e1e;
    padding: 5px;
    padding-top: 3px;
    padding-bottom: 3px;
    margin: 0px;
}
}
