@namespace url(http://www.w3.org/1999/xhtml);
/* i really want this to be global */

sejspan#sej-container {
position: fixed;
top: 55px;
right: 10px;
width: 40px;
height: 18px !important;
z-index: 99999 !important;
background: rgba(49, 255, 8, .8);

display: block !important;
padding: 5px 10px !important;
transition: all .5s ease .5s;
}
sejspan#sej-container>sejspan {transition: all .5s ease .5s;}

sejspan#sej-container:hover {z-index: 99999 !important; width: 50%; height: 50px !important; transition: all 0s ease 0s; padding-left: 60px !important; background: rgba(255, 255, 255, .95);}

sejspan#sej-container:hover>sejspan {margin-left: -50px; transition: all 0s ease 0s; }
sejspan#sej-container>a {display: inline-block; min-width: 30px !important; visibility: hidden; padding: 0 4px; transition: all .5s ease .6s;}
sejspan#sej-container:hover>a {visibility: visible; transition: all 0s ease 0s;}
sejspan#sej-container>a:hover {background: #FFFFCC;}

sejspan#sej-drop-down-list-trigger {z-index: 100000 !important;}
sejspan#sej-drop-down-list-trigger>a {padding: 3px;}
