css:
@-moz-document url-prefix("http://www.eldarya.fr/") {

#contentPane > div {
text-align: center !important;}


.tooltip-content {
    position: fixed;
    font-family: Nilland, serif;
    color: white;
    background-color: #894100;
    border: 1px solid #FC3050;
    padding: 10px;
    border-radius: 6px;
}
.tooltip-content {
    position: fixed;
    font-family: Nilland, serif;
    color: white;
    background-color: #894100;
    border: 1px solid #894100;
    padding: 10px;
    border-radius: 6px;
    text-shadow: 0px 0px 1px #fff6d3 !important;
}
.flavr-button.gold,
.button.button.button-gold {
    background-color: #9ED41E;
    border-color: #9ED41E;
}
.flavr-button.gold,
.button.button.button-gold:hover,
.flavr-button.gold,
.button.button.button-gold.selected {
    background-color: white !important;
    color: #9ED41E !important;
}
.flavr-button.purple,
.button.button.button-purple:hover,
.flavr-button.purple,
.button.button.button-purple.selected {
    background-color: white !important;
    color: #FC3050 !important;
}
#header-login-form input {
    color: #FC3050;
    font-family: Banda, sans-serif;
    margin: 0 3px;
}
body {
    background: #E4DBD4 url("http://i.imgur.com/giCtMRM.jpg") fixed top center;
    background-size: cover;
    overflow-y: scroll;
}
#header #header-top {
    position: absolute;
    top: 0;
    background-color: #FC3050;
    height: 30px;
    width: 100%;
}
#header-menu a {
    font-family: Nilland, sans-serif;
    color: #fff0f8;
}
#footer {
    font-family: Nilland, sans-serif;
    background-color: #FC3050;
}
#footer #footer-links a {
    color: #fff0f8;
}
#footer #footer-in {
    color: #fff0f8;
}
#header {
    height: 167px;
    position: relative;
    background-color: rgba(255, 255, 255, 0.5);
}
#player-headerPortrait {
    border: solid 4px #894100;
    box-shadow: 0 0 5px 0 #2196F3;
    background-color: #FC3050;
}
#pet-headerPortrait {
    top: 35px;
    height: 73px;
    width: 73px;
    border-radius: 50%;
    border: solid 4px #894100;
    box-shadow: 0 0 5px 0 #2196F3;
    background: #894100;
    position: absolute;
    left: -10px !important
}
#player-name {
    font-family: Nilland, sans-serif;
    color: #fff;
    font-weight: 600;
}
#player-info-level {
    font-family: Candara;
    color: #FC3050;
    background-color: #9ED41E;
    box-shadow: 0 0 5px 0 #2196F3;
}
#player-info-dots .player-info-dot {
    background-color: #FC3050;
    box-shadow: 0 0 5px 0 rgba(25, 25, 25, 0.25);
}
#player-info-dots .player-info-dot.lit {
    background-color: #9ED41E;
    box-shadow: 0 0 5px 0 rgba(0, 228, 255, 0.62);
}
#player-infos .player-currencies {
    background-color: #9ED41E;
    border: 1px solid;
    border-color: #9ED41E;
}
.tile-title {
    height: 37px;
    color: #fff;
    line-height: 37px;
    font-size: 17px;
    font-family: Amatic, serif;
    font-weight: 400;
    text-align: center;
}
img[src="/i18n/static/fr/logo.png"] {
    width: 0;
    height: 0;
    background: url("http://i.imgur.com/WVlvJ6o.png");
    padding-left: 431px;
    padding-top: 165px;
    position: absolute;
    top: -0px;
    left: -130px;
}
.index-carousel {
    font-family: Nilland;
    border: 1px solid #C8B5AB;
    background-color: #E4DBD4;
    overflow: hidden;
}
.index-carousel-title {
    font-family: Amatic, serif;
    color: white;
}
.index-carousel-subtitle {
    border-top: 1px solid #ec5e82;
    font-family: "Nilland", serif;
    color: #ec5e82;
}
.index-carousel-description {
    text-shadow: 0px 0px 2px white, 0px 0px 2px white !important;
    font-family: "Nilland", serif;
    text-align: justify;
    height: 80px;
}
.index-main-container .index-carousel-description {
    color: white;
}
.flavr-button.purple,
.button.button.button-purple {
    background-color: #f18ea7;
    border: 1px solid;
    border-color: #C8B5AB;
}
#ranking-list > div {
    background: #E4DBD4 no-repeat 20px;
    background-size: 75px;
    position: relative;
    border-top: 1px solid #E4DBD4;
    box-shadow: inset 0px 0px 2px rgba(0, 0, 0, 1), 0px 0px 5px #00fff6 !important;
}
#ranking-list .guard-4 {
    background: #C8B5AB url('http://www.eldarya.fr/static/img/design/guard/guard-4-big.png') no-repeat 5px;
    background-size: 90px;
    box-shadow: inset 0px 0px 2px rgba(0, 0, 0, 1), 0px 0px 5px #00fff6 !important;
}
#index-ranking {
    background-color: #442101;
    border: 1px solid #E4DBD4;
}
#news-list .news {
    background: #E4DBD4 no-repeat 20px;
    position: relative;
    border-top: 1px solid #E4DBD4;
    height: 50px;
    box-shadow: inset 0px 0px 2px rgba(0, 0, 0, 1), 0px 0px 5px #00fff6 !important;
}
#index-news .text a {
    color: #FFFFFF;
}
#index-news {
    background-color: #442101;
    border-radius: 10px;
    border: 1px solid #E4DBD4;
}
.tile-title {
    color: #C8B5AB;
}
#index-news .date {
    float: left;
    background-color: #442101;
    width: 55px;
    height: 25px;
    color: #FFFFFF;
    border-radius: 5px;
    font-size: 14px;
    text-align: center;
}
#index-player {
    border: solid 1px #C8B5AB;
    background-color: #fff;
}

#main-menu .main-menu-episode {

    background-image: url("http://img15.hostingpics.net/pics/170979coeur.png");
  -webkit-filter: hue-rotate(200deg);
    filter: hue-rotate(200deg);

}

#main-menu .main-menu-minigames {

    background-image: url("http://img15.hostingpics.net/pics/790708puzzle.png");
  -webkit-filter: hue-rotate(200deg);
    filter: hue-rotate(200deg);

}

#main-menu .main-menu-mall {

    background-image: url("http://img15.hostingpics.net/pics/379788shop.png");
  -webkit-filter: hue-rotate(200deg);
    filter: hue-rotate(200deg);

}

#main-menu .main-menu-marketplace {

    background-image: url("http://img15.hostingpics.net/pics/860091marteau.png");
  -webkit-filter: hue-rotate(200deg);
    filter: hue-rotate(200deg);

}

#main-menu .main-menu-ranking {

    background-image: url("http://img15.hostingpics.net/pics/433682trophe769e.png");
  -webkit-filter: hue-rotate(200deg);
    filter: hue-rotate(200deg);

}

#main-menu .main-menu-library {

    background-image: url("http://img15.hostingpics.net/pics/153285livre.png");
  -webkit-filter: hue-rotate(200deg);
    filter: hue-rotate(200deg);

}

#main-menu .main-menu-messaging {

    background-image: url("http://img15.hostingpics.net/pics/879316lettre.png");
  -webkit-filter: hue-rotate(200deg);
    filter: hue-rotate(200deg);
}

#main-menu .main-menu-bank {

    background-image: url("http://img15.hostingpics.net/pics/267696diamand.png");
  -webkit-filter: hue-rotate(200deg);
    filter: hue-rotate(200deg);

}




#main-menu .menu-label span {
    background-color: #302765;
}
#main-menu .menu-label {
    color: #C8B5AB;
}
#main-menu .main-menu-messaging .menu-label span {
    background-color: #383173;
}
#main-menu .main-menu-bank .menu-label span {
    background-color: #3C3373;
}
#library-npc-crush-heart {
    position: absolute;
    left: -14px;
    top: -10px;
    width: 30px;
    height: 23px;
    background: url("http://img11.hostingpics.net/pics/452048affectionfull.png") 0% 0% / 100%;
}
img[src="/static/img/design/m.png"] {
    width: 0 !important;
    height: 0 !important;
    background: url("http://img15.hostingpics.net/pics/21275116711012m.png") !important;
    padding-left: 14px !important;
    padding-top: 15px !important;
}
img[src="/static/img/design/f.png"] {
    width: 0 !important;
    height: 0 !important;
    background: url("http://img15.hostingpics.net/pics/52677834316483f.png") !important;
    padding-left: 9px !important;
    padding-top: 15px !important;
}
#pet-info-affection-level div {
    height: 25px;
    background: url('http://img15.hostingpics.net/pics/370787affectionfull.png');
}
#pet-info-affection-level {
    display: inline-block;
    width: 150px;
    height: 23px;
    background: url('http://img15.hostingpics.net/pics/806978affectionempty.png');
}
#pet-info-luck-level {
    display: inline-block;
    width: 30px;
    min-height: 30px;
    padding-left: 35px;
    background: url('http://img15.hostingpics.net/pics/292218278157luckfull.png') no-repeat left;
    margin-left: 6px;
}
#pet-info-evolve div.on {
    color: #ffffff;
    font-weight: bold;
    text-shadow: 0px 0px 2px #feac11, 0px 0px 3px #feac11 !important;
}
#pet-info-food,
#pet-info-levels {
    background: rgba(116, 149, 186, 0.7) !important;
}
#pet-info-energy-level,
#event-bonus-progressbar-fill {
    background-color: #9ED41E;
}
#messageBarLevel {
    background-color: #9ED41E;
}
#episode-list > li .episode-conditions li.on {
    color: white;
    font-weight: bold;
    text-shadow: 0px 0px 2px #feac11, 0px 0px 3px #feac11 !important;
}



}
