css:
#querytime, #copyright, #extensions-used, #brd-stats {
display: none;
}

.author-info {
display: none;
}

.post-options, .post-options a:link, .post-options a:visited {
color: #ccc;
font-size: 90%;
height: 1.8em !important;
padding: 0 5px;
line-height: 1em;
}

.post {
border: 1px solid #eee !important;
}

.post:hover  {
margin-bottom: 0px ;
}

.postfoot {
border: 0 !important;
}

.post-options {
background-color: #fff !important;
height: 15px !important;
}

.post-actions a:link, .post-actions a:visited {
color: #eee;
}
.post-contacts {
text-align: left;
}

.post-actions, .post-contacts {
display: none !important;
line-height: 1em !important;
padding: 0  !important;
margin: 0 !important;
}

.post:hover .post-actions,
.post:hover .post-contacts{
display: block !important;
}

.post-num {
position: absolute;
left: -60px;
font-size: 2em;
color: #eee;
}



.post {
margin: 2px !important;
}

.post-link {
float: right;
background-color: #fff !important;
border: 0 !important;
font-size: 80% !important;
}

.post-link a:link, .post-link a:visited{
color: #ccc !important;
text-decoration: none;
margin-right: 5px;
}

.post-ident {
background-color: #fff !important;
border: 0 !important;
height: 0 !important;
padding: 0 !important;
}

.posthead {
background-color: #fff !important;
}

.postbody {
background-color: #fff !important;
padding: 0 18px 0 12px !important;
margin: 0;
}

.post-byline {
display: none !important;
}
.useravatar {
display: block !important;
}
.username {
display: block !important;
}
.usertitle {
font-weight: normal !important;
font-size: 90%;
color: #ccc;
}
.userstatus {
display: none !important;
}

.main-subhead h2 {
display: none !important;
}

.frm-buttons {
border: 0 !important;
}
