css:
@-moz-document domain("docs.meteor.com"), domain("guide.meteor.com") {
body {
    color: #ddd;
    background-color: #333;
}

.link.primary:link, .link.primary:visited, .sidebar ul.list-toc a.current:link, .sidebar ul.list-toc a.current:visited {
    color: #ff5e5b;
}

/* Panel */

.panel {
    background: #111;
}

.panel .panel-item a:link, .panel .panel-item a:visited, a:visited, .link:visited {
    color: #ddd;
}

.panel .panel-item svg > *, .panel .panel-item img > * {
    fill: #aaa;
    stroke: #aaa;
}

/* Sidebar */

.sidebar .sidebar-content {
    background-color: #222;
}

.sidebar ul.list-toc a:link, .sidebar ul.list-toc a:visited {
    color: #ddd;
}

.sidebar ul.list-toc a.active, .sidebar ul.list-toc a:hover {
    color: #ff5e5b !important;
    transform: rotate(0.1deg);
}

.sidebar .sidebar-content::-webkit-scrollbar-thumb {
    background: rgba(255, 255, 255, 0.15);
}

.sidebar .sidebar-content::-webkit-scrollbar-track {
    background: rgba(0, 0, 0, 0.10);
}

/* Content */

.content {
    background: #333;
}

.content .pagination {
    border-top-color: #777;
    border-bottom-color: #777;
}

.content .api {
    background: #111;
    border: 1px solid #555;
}

.content .api .api-heading {
    background: #222;
}

.content .api .api-heading .locus {
    color: #bbb;
}
}
