css:
@-moz-document 
/* Googlereader
   GooderDalilV0.5
   By : Morteza Dalil
   http://sarsum.com */
url-prefix(http://www.google.com/reader),
url-prefix(https://www.google.com/reader)
{
#entries.list #current-entry.expanded .entry-actions {
border-color:#8181DC;
clear:both;
}

#current-entry.read .entry-container .entry-body {
color:#000000;
line-height:20px;
float:right;
}
.read .entry-container .entry-body {
color:#222222;
line-height:20px;

}
#entries{
background-color:#EBF2FE;
}

.entry .entry-title a{
padding:0px;
float:right;
font-size:80%;
}
.entry .entry-title .entry-title-go-to {
border:0 none;
float:right;
padding-left:0.25em;
vertical-align:top;
}
.entry .card .entry-container .entry-date {
float:right;
width:100%;
}
#current-entry.read .entry-container .entry-body {
float:right;
clear:both;
}

#entries.list .read .collapsed {
background:#EEEEEE none repeat scroll 0 0;
border:2px solid #ffffff;
}
#entries.list .entry  {
-moz-user-select:none;
background:#FFFFFF none repeat scroll 0 0;
border:4px solid #EBF2FE;
cursor:pointer;
margin:0;
overflow:hidden;
padding:1px 0;
position:relative;
width:auto;
}
#entries.list #current-entry.expanded .entry-actions {
border-color:#8181DC;
float:left;
}
#current-entry.overflow-settable .entry-main {
margin-left:26em;
overflow:auto;
}
.entry .entry-main {
float:right;
margin-left:20px;
}
.entry .entry-main {
margin-left:26em;
}
#entries.list .entry {
border-bottom:1px solid #AAAAAA;
font-family:tahoma;
font-size:11px;
margin:0;
overflow:hidden;
padding:0;
}
#entries.list .entry:hover{
-moz-user-select:none;
background:#E6E6E6 none repeat scroll 0 0;
border:2px solid #FFFFFF;
cursor:pointer;
margin:0;
overflow:hidden;
padding:3px 0;
position:relative;
width:auto;

}

#entries.list .collapsed .entry-icons {
display:block;
left:0.2em;
margin:0;
overflow:visible;
padding:0;
position:absolute;
top:1px;
width:auto;
}
#entries.list .collapsed .entry-date {
display:block;
margin:1.2em 0.7em 1.2em 0.2em;
width:auto;
}
#entries.list .collapsed .entry-main {
display:block;
float:none;
margin:0 5em 0 0;
padding:0;
}
#main {
  font-family:tahoma,arial,sans-serif !important;
  margin-top:0 !important;
  padding:0px !important;
  border:0px !important;
}
#nav {
  width:260px !important;
  float:right !important;
  margin:0.3em 5px 0px 0 !important;
}
#chrome {
  border-left:none !important;
  border-right:1px solid #C2CFF1;
  margin-right:265px !important;
  margin-left:0px !important;
  padding-top:0.5em !important;
}
.lhn-hidden #chrome {
  margin-right:0 !important;
}
#chrome-viewer {
  margin-right:8px !important;
  margin-left:0px !important;
}
#sub-tree {
  padding:0pt 8px 0pt 0pt !important;
  text-align:right;
  width: 250px !important;
}
#sub-tree .toggle {
  left:auto !important;
  right:-18px !important;
}
#sub-tree .icon {
  padding-right:0px !important;
  padding-left:3px !important;
  padding-top:2px !important;
}
#sub-tree a {
  direction:rtl;
  font-size:85%;
}
#sub-tree ul {
  padding-right:12px !important;
  padding-left:0px !important;
}
#sub-tree li {
  clear:right !important;
}
#sub-tree li a {
  float:right !important;
  padding:0px 0px 0px 5px !important;
}
#sub-tree .name {
  padding-right:20px !important;
  padding-left:1px !important;
}
.rtl {
  text-align:justify;
}
.entry-main {
  margin-right: 10% !important;
  margin-left: 10% !important;
}
.entry-title {
  direction:rtl;
  text-align:right;
  max-width:100% !important;
  width:100% !important;
}
.entry-author {
  direction:ltr !important;
  text-align:right !important;
}
.entry-body {
  max-width:100% !important;
  line-height:1.7em;
  padding-top:2em !important;
}
#search {
  position:absolute !important;
  top:30px !important;
  left:150px !important;
  z-index:1000 !important;
}
#search-input {
  font-family:tahoma, arial !important;
  background-color:#FFFADB !important;
  padding: 2px 2px !important;
}
#entries.list .collapsed .entry-main .entry-source-title {
display:block;
float:none;
left:1.85em;
margin-top:20px;
overflow:hidden;
padding:0 1em 0 0;
position:absolute;
top:1px;
white-space:nowrap;
width:13em;
}
#entries.list .collapsed .entry-secondary {
display:block;
float:none;
left:0;
margin:0 9em 0 20em;
overflow:hidden;
padding:0;
position:absolute;
top:1px;
white-space:normal;
width:auto;
}
#entries.list .collapsed .entry-secondary .entry-title {
display:inline;
font-family:tahoma;
font-family:tahoma;
font-size:12px;
margin:0;
padding:0;
position:static;
white-space:inherit;
width:auto;

}
#entries.list .collapsed .entry-secondary .snippet {
display:inline;
float:none;
margin:0;
padding:0;
position:static;
white-space:inherit;
width:auto;
}
#current-entry.read .entry-container .entry-body {
color:#000000;
float:right;
margin-right:8em;
}

.entry .card .c {
background-color:#FFFFFF;
background-repeat:no-repeat;
border:0 none;
font-family:tahoma;
padding:0;
}
#sub-tree {
direction:rtl;
font-family:tahoma;
font-size:10px;
height:420px;
overflow-x:hidden;
overflow-y:auto;
width:255px;
}
#current-entry.overflow-settable .entry-main {
overflow:auto;
}

}
