css:
@namespace "http://www.w3.org/1999/xhtml";
@-moz-document domain("rumedia.ws") {
body {
    background: #000 url("http://rumedia.ws/templates/rum/images/fon2.jpg") no-repeat scroll center top;
    color: #a5bbdc;
    font-family: Raleway,DIN Pro,Verdana,Helvetica,Arial,sans-serif;
    font-weight: 100;
    padding: 0;
    text-align: center;
}
h1, h2, h3, h4 {
    font-size: 12px;
    margin: 0;
    padding: 0;
}
a {
    text-decoration: none;
}
img {
    border: medium none;
}
.login-panel {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/login-panel.png") no-repeat scroll 0 0;
    display: none;
    height: 203px;
    left: 50%;
    margin: -101px 0 0 -115px;
    position: fixed;
    top: 50%;
    width: 230px;
    z-index: 100;
}
.login-block {
    height: 203px;
    position: relative;
    width: 230px;
}
.close {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/close.jpg") no-repeat scroll 0 0;
    cursor: pointer;
    height: 19px;
    position: absolute;
    right: 5px;
    top: 5px;
    width: 19px;
}
.shadow-fon {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/shadow-fon.png") repeat scroll 0 0;
    display: none;
    height: 100%;
    left: 0;
    position: fixed;
    top: 0;
    width: 100%;
}
.main {
    margin: 0 auto;
    text-align: left;
    width: 1000px;
}
.header {
}
.head-block {
    background: #24303d none repeat scroll 0 0;
    border: 1px solid #364451;
    height: 87px;
    margin: 5px;
    position: relative;
}
.logo {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/logo5.png") no-repeat scroll 0 0;
    display: block;
    height: 75px;
    left: 7px;
    position: absolute;
    top: 6px;
    width: 217px;
}
.banner {
    border: 5px solid #5d5d5d;
    border-radius: 5px;
    height: 60px;
    left: 207px;
    position: absolute;
    top: 10px;
    width: 467px;
}
.header_icons {
    border-radius: 5px;
    height: 50px;
    left: 400px;
    position: absolute;
    top: 20px;
    width: 467px;
}
.header_icons a {
    display: inline-block;
    position: relative;
}
.header_icons a:hover {
    top: 2px;
}
.header_icons a::before {
    bottom: -10px;
    content: "";
    left: -10px;
    position: absolute;
    right: -10px;
    top: -10px;
    z-index: -1;
}
.header_icons a:active::before {
    top: -11px;
}
.login-enter {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/enter-fon.png") repeat-x scroll 0 0;
    color: #000;
    cursor: pointer;
    display: block;
    font: bold 12px/25px Arial;
    height: 25px;
    left: 703px;
    position: absolute;
    text-align: center;
    top: 30px;
    width: 112px;
}
.login-enter:hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/enter-fon.png") repeat-x scroll 0 bottom;
    color: #fff;
}
.top-menu-block {
    background: #000000 none repeat scroll 0 0;
    border: 1px solid #364451;
    border-radius: 3px;
    height: 46px;
    line-height: 46px;
    padding: 0 0 0 5px;
    z-index: 1;
}
.top-menu-link {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-orange.png") repeat-x scroll 0 0;
    border-radius: 2px;
    color: #fff;
    display: block;
    float: left;
    font: bold 12px/28px Arial;
    height: 28px;
    margin: 9px 9px 0 0;
    padding: 0 12px 0 14px;
    text-transform: uppercase;
}
.top-menu-link:hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-orange.png") repeat-x scroll 0 bottom;
}
.green.top-menu-link {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-green.png") repeat-x scroll 0 0;
}
.green.top-menu-link:hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-green.png") repeat-x scroll 0 bottom;
}
.akva.top-menu-link {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-akva.png") repeat-x scroll 0 0;
}
.akva.top-menu-link:hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-akva.png") repeat-x scroll 0 bottom;
}
.fiolet.top-menu-link {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-fiolet.png") repeat-x scroll 0 0;
}
.fiolet.top-menu-link:hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-fiolet.png") repeat-x scroll 0 bottom;
}
.oran.top-menu-link {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-oran.png") repeat-x scroll 0 0;
}
.oran.top-menu-link:hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-oran.png") repeat-x scroll 0 bottom;
}
.blue.top-menu-link {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-blue.png") repeat-x scroll 0 0;
}
.blue.top-menu-link:hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-blue.png") repeat-x scroll 0 bottom;
}
.pink.top-menu-link {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-pink.png") repeat-x scroll 0 0;
}
.pink.top-menu-link:hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-pink.png") repeat-x scroll 0 bottom;
}
.salat.top-menu-link {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-salat.png") repeat-x scroll 0 0;
}
.salat.top-menu-link:hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-salat.png") repeat-x scroll 0 bottom;
}
.ser.top-menu-link {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-ser.png") repeat-x scroll 0 0;
}
.ser.top-menu-link:hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/link-ser.png") repeat-x scroll 0 bottom;
}
.hideWrap {
    background-color: #141414;
    clear: both;
    text-align: left;
}
.hideBtn {
    background: transparent url("http://rumedia.ws/templates/rum/images/slider-show.png") no-repeat scroll left center;
    color: #ffffff;
    cursor: pointer;
    font: 11px/15px Arial;
    height: 15px;
    margin-left: 842px;
    outline: medium none;
    overflow: hidden;
    padding: 1px 14px 2px;
    position: absolute;
    width: 150px;
    z-index: 1;
}
.hideBtn.close {
    background: transparent url("http://rumedia.ws/templates/rum/images/slider-show.png") no-repeat scroll left center;
    color: #ffffff;
    cursor: pointer;
    display: inline-block;
    font: 11px/15px Arial;
    height: 15px;
    margin-left: 847px;
    margin-top: -5px;
    outline: medium none;
    overflow: hidden;
    padding: 1px 14px 2px;
    position: relative;
    width: 150px;
    z-index: 1;
}
.slider {
    height: 468px;
    position: relative;
    width: 1000px;
    z-index: 0;
}
.slider-item {
}
.slider-item-big {
    display: none;
    height: 468px;
    left: 0;
    overflow: hidden;
    position: absolute;
    top: 0;
    width: 1000px;
}
.slider-item-big img {
    border: 0 none;
    left: 0;
    min-height: 468px;
    position: absolute;
    top: 0;
    width: 1000px;
}
.dark2 {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/dark2.png") repeat scroll 0 0;
    border: 0 none;
    bottom: 0;
    height: 144px;
    left: 0;
    position: absolute;
    width: 1000px;
}
.slider-title {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/dark2.png") repeat scroll 0 0;
    bottom: 168px;
    padding: 10px;
    position: absolute;
    right: 15px;
    width: 466px;
}
.slider-title h2 {
    color: #fff;
    font-size: 28px;
    font-weight: normal;
    padding: 0 0 10px;
    text-align: right;
    text-transform: uppercase;
}
.slider-title-content {
    color: #fff;
    font-size: 11px;
    line-height: 16px;
    padding: 0 0 10px;
    text-align: right;
}
.slider-title-more {
    padding: 0 0 10px;
}
.slider-title-more a {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/slider-title-more.png") no-repeat scroll left top;
    float: right;
    height: 25px;
    width: 118px;
}
.slider-title-more a:hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/slider-title-more.png") no-repeat scroll left bottom;
}
.slider-item-small {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/slider-item-small.png") no-repeat scroll left bottom;
    float: left;
    height: 119px;
    margin: 335px 0 0 17px;
    position: relative;
    width: 180px;
    z-index: 99999;
}
.active-slider .slider-item-small, .slider-item-hover {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/slider-item-small.png") no-repeat scroll left top !important;
}
.slider-item-small-image {
    cursor: pointer;
    height: 88px;
    left: 5px;
    overflow: hidden;
    position: absolute;
    top: 5px;
    width: 168px;
}
.slider-item-small-image img {
    border: 0 none;
    min-height: 88px;
    width: 168px;
}
.activsl {
    display: block;
}
.content-block {
    background: #141414 none repeat scroll 0 0;
    padding: 7px 6px 10px 8px;
}
.center-block {
    background: #202a36 none repeat scroll 0 0;
    float: left;
    margin: 0 0 0 4px;
    width: 339px;
}
.center-block:first-child {
    margin: 0;
}
.top-center-block {
    background: #222933 none repeat scroll 0 0;
    padding: 9px 0 0;
}
.bottom-center-block {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 center;
    padding: 0 0 9px;
}
.new-reviews {
    margin: 0 9px 8px 10px;
}
.new-reviews-title-block {
    background: #5b90b2 none repeat scroll 0 0;
    border-radius: 0;
    color: #fff;
    font: bold 12px/35px Raleway,DIN Pro,Verdana,Helvetica,Arial,sans-serif;
    height: 35px;
    padding: 0 12px 0 14px;
    text-transform: uppercase;
    width: 296px;
}
.all-reviews {
    color: #16262d;
    display: block;
    float: right;
    font: 11px/35px Raleway,DIN Pro,Verdana,Helvetica,Arial,sans-serif;
    text-decoration: underline;
    text-transform: none;
}
.all-reviews:hover {
    text-decoration: none;
}
.fiolet.new-reviews-title-block {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/title-fiolet.png") repeat-x scroll 0 0;
}
.salat.new-reviews-title-block {
    background: #0782ab none repeat scroll 0 0;
}
.new-reviews-content-block {
}
.new-reviews-news {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/fon-new-reviews.png") no-repeat scroll 0 0;
    border: 1px solid #364451;
    border-radius: 2px;
    height: 64px;
    margin: 7px 0 0;
    width: 319px;
}
.new-reviews-news-image {
    background: #24303d none no-repeat scroll 0 0;
    border: 1px solid #364451;
    border-radius: 2px;
    float: left;
    height: 47px;
    margin: 8px 12px 0 8px;
    overflow: hidden;
    position: relative;
    width: 68px;
}
.new-reviews-image-fon {
    display: block;
    height: 40px;
    left: 5px;
    overflow: hidden;
    position: relative;
    top: 4px;
    width: 56px;
}
.new-reviews-image-fon img {
    left: 0;
    min-height: 40px;
    position: absolute;
    top: 0;
    width: 56px;
}
.new-reviews-news h2 a {
    color: #ccc;
    display: block;
    float: left;
    font: 13px Raleway,DIN Pro,Verdana,Helvetica,Arial,sans-serif;
    height: 45px;
    margin: 10px 0 0;
    overflow: hidden;
    width: 225px;
}
.new-reviews-news h2 a:hover {
    color: #58bdc7;
}
.new-edition-news {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/line-edition.png") repeat-x scroll 0 bottom;
    margin: 16px 0 0;
    padding: 0 0 2px;
}
.new-edition-news:first-child {
    margin: 22px 0 0;
}
.new-edition-news h2 a {
    color: #fff;
    display: block;
    font: bold 12px Arial;
    height: 15px;
    margin: 0 0 10px;
    overflow: hidden;
}
.new-edition-news h2 a:hover {
    text-decoration: underline;
}
.new-edition-news-image {
    border: 4px solid #373737;
    float: left;
    height: 71px;
    margin: 0 12px 11px 0;
    width: 134px;
}
.new-edition-image-fon {
    border: 1px solid #7d7d7d;
    display: block;
    height: 69px;
    left: 0;
    overflow: hidden;
    position: relative;
    top: 0;
    width: 132px;
}
.new-edition-image-fon img {
    left: 0;
    min-height: 69px;
    position: absolute;
    top: 0;
    width: 132px;
}
.new-edition-content {
    color: #8e8e8e;
    float: left;
    font: 11px/16px Arial;
    height: 79px;
    overflow: hidden;
    width: 160px;
}
.right-center-block {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/right-repeat-center-block.jpg") repeat-y scroll 0 0;
    float: left;
    margin: 0 0 0 4px;
    width: 300px;
}
.right-top-center-block {
    background: #2d283f none repeat scroll 0 0;
    padding: 9px 0 0;
}
.right-bottom-center-block {
    background: #2d283f none repeat scroll 0 0;
    padding: 0 0 9px;
}
.video {
    margin: 0 0 8px 10px;
}
.video-title-block {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/title-green.png") repeat-x scroll 0 0;
    border-radius: 3px;
    color: #fff;
    font: bold 12px/35px Arial;
    height: 35px;
    margin: 0 10px 0 0;
    padding: 0 12px 0 14px;
    text-transform: uppercase;
    width: 253px;
}
.video-content-block {
    margin: 12px 0 11px;
}
.video-news {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/fon-video.png") no-repeat scroll 0 0;
    float: left;
    height: 126px;
    margin: 0 8px 8px 0;
    width: 135px;
}
.video-news:first-child {
    margin: 0 8px 0 0;
}
.video-news-image {
    height: 76px;
    left: 7px;
    overflow: hidden;
    position: relative;
    top: 6px;
    width: 121px;
}
.video-news-image img {
    left: 0;
    min-height: 76px;
    position: absolute;
    top: 0;
    width: 121px;
}
.video-news h2 a {
    color: #737373;
    display: block;
    font: 11px Arial;
    height: 30px;
    margin: 13px 0 0 7px;
    overflow: hidden;
    text-decoration: underline;
    width: 121px;
}
.video-news h2 a:hover {
    color: #fff;
}
.tizer {
    margin: 0 0 8px 10px;
}
.tizer-title-block {
    background: #5b7399 none repeat scroll 0 0;
    border-radius: 0;
    color: #fff;
    font: bold 12px/35px Arial;
    height: 35px;
    margin: 0 10px 0 0;
    padding: 0 12px 0 14px;
    text-transform: uppercase;
    width: 253px;
}
.tizer-content-block {
    margin: 12px 0 11px;
}
.tizer-news {
    background: rgba(0, 0, 0, 0) url("http://rumedia.ws/templates/rum/images/fon-tizer.png") no-repeat scroll 0 0;
    border: 1px solid #364451;
    border-radius: 3px;
    float: left;
    height: 260px;
    margin: 0 8px 8px 0;
    width: 135px;
}
.tizer-news:first-child {
    margin: 0 8px 0 0;
}
.tizer-news-image {
    height: 190px;
    left: 7px;
    overflow: hidden;
    position: relative;
    top: 6px;
    width: 121px;
}
.tizer-news-image img {
    left: 0;
    max-height: 180px;
    position: relative;
    top: 0;
    width: 121px;
}
.tizer-news h2 a {
    color: #737373;
    display: block;
    font: 11px Arial;
    height: 30px;
    margin: 0 0 0 7px;
    overflow: hidden;
    text-decoration: underline;
    width: 121px;
}
.tizer-news h2 a:hover {
    color: #fff;
}
.tizer-news strong a {
    color: #ffffff;
    display: block;
    font: 700 12px "Arial";
    height: 15px;
    margin: 5px 5px 0;
    overflow: hidden;
}
.tizer-news strong a:hover {
    text-decoration: underline;
}
.left-col {
    float: left;
    margin: 8px 0 0;
    width: 680px;
}
.shortstory-block {
    background: #222933 none repeat scroll 0 0;
}
.shortstory-block-top {
    background: #222933 none repeat scroll 0 0;
    padding: 6px 0 5px;
}
.main-news {
    background: #24303d none repeat scroll 0 0;
    border: 1px solid #364451;
    height: 320px;
    margin: 5px 0 0 5px;
    position: relative;
    width: 667px;
}
.main-news:hover {
    border: 1px solid #666;
    box-shadow: 0 0 10px 0 rgba(4, 162, 220, 0.5);
    z-index: 100;
}
.main-speedbar {
    background: #24303d none repeat scroll 0 0;
    border: 1px solid #364451;
    border-radius: 1px;
    color: #ccc;
    margin: 0 0 2px 9px;
    min-height: 20px;
    overflow: hidden;
    padding: 5px 0 0 5px;
    width: 656px;
}
.main-speedbar a {
    color: #fff;
}
.main-speedbar a:hover {
    color: #f26e00;
}
.main-sort {
    height: 20px;
    margin: 5px 0 0 5px;
    padding: 12px 0 0 10px;
    width: 672px;
}
.main-news-all {
    padding: 12px 0 0 10px;
}
.main-news-image {
    background: #000 none repeat scroll 0 0;
    border: 1px solid #364451;
    float: left;
    height: 208px;
    margin: 0 0 0 5px;
    position: relative;
    width: 158px;
}
.main-news-image:hover {
    border: 1px solid #777;
    box-shadow: 0 0 10px 0 rgba(4, 162, 220, 0.5);
    z-index: 100;
}
.main-news-img-fon {
    display: block;
    height: 200px;
    left: 4px;
    overflow: hidden;
    position: relative;
    top: 4px;
    width: 150px;
}
.main-news-img-fon img {
    left: 0;
    position: absolute;
    top: 0;
    transform: scale(1);
    transition: all 0.6s ease 0s;
    width: 150px;
}
.main-news-img-fon img:hover {
    transform: scale(1.2);
}
.right-col-main-news {
    float: left;
    height: 125px;
    margin: 0 0 0 11px;
    width: 465px;
}
.right-col-main-news img {
}
.right-col-main-news h1 a {
    color: #fff;
    display: block;
    font: bold 15px Arial;
    margin: 4px 0 0;
    overflow: auto;
    text-shadow: 0 0 0.2em #87f, 0 0 0.2em #87f, 0 0 0.2em #87f;
}
.right-col-main-news h1 a:hover {
    text-decoration: underline;
}
.right-col-main-news h2 a {
    color: #fff;
    display: block;
    font: bold 13px Arial;
    margin: 4px 0;
    overflow: auto;
    text-shadow: 0 0 0.2em #f87, 0 0 0.2em #f87, 0 0 0.2em #f87;
}
.right-col-main-news h2 a:hover {
    text-decoration: underline;
}
.size-foto img {
    height: 120px;
    left: 4px;
    position: absolute;
    top: 4px;
    width: 214px;
}
.main-news-content {
    background: #222933 none repeat scroll 0 0;
    border: 1px solid #364451;
    border-radius: 3px;
    color: #a5bbdc;
    font: 300 13px Verdana,Helvetica,Arial,sans-serif;
    height: 219px;
    overflow: hidden;
    padding: 5px 0 0 7px;
    width: 470px;
}
.main-news-content-forum img {
    padding-right: 5px;
}
.main-news-content-forum {
    color: #ccc;
    font: 12px/16px Arial;
    height: 190px;
    overflow: hidden;
    width: 640px;
}
.main-news-content br {
    display: none;
}
.main-news-info {
    height: 47px;
    position: relative;
    top: 257px;
    width: 655px;
}
.main-news-more2 a {
    background: #c24310 none repeat scroll 0 0;
    border: 1px solid #1f88b8;
    color: #fff;
    display: block;
    font: bold 11px/30px Arial;
    height: 30px;
    left: 5px;
    position: absolute;
    text-align: center;
    text-transform: uppercase;
    top: 9px;
    transition: all 0.3s ease 0s;
    width: 160px;
}
.main-news-more2 a:hover {
    background: #ff6347 none repeat scroll 0 0;
}
.views-com {
    color: #323232;
    font: 11px Arial;
    left: 198px;
    position: absolute;
    top: 18px;
}
.views-com span a {
    color: #323232;
    text-decoration: underline;
