    padding: 4px;
}
/* ***** basic styles for multi-page nav elements */

.pagenav a {
    text-decoration: none;
}
.pagenav td {
    padding: 2px 4px 2px 4px;
}
/* ***** de-emphasized text */

.shade,
a.shade:link,
a.shade:visited {
    color: #777777;
    text-decoration: none;
}
a.shade:active,
a.shade:hover {
    color: #FF4400;
    text-decoration: underline;
}
.tcat .shade,
.thead .shade,
.tfoot .shade {
    color: #DDDDDD;
}
/* ***** define margin and font-size for elements inside panels ***** */

.fieldset {
    margin-bottom: 6px;
}
.fieldset,
.fieldset td,
.fieldset p,
.fieldset li {
    font-size: 11px;
}
/* ***** don't change the following ***** */

form {
    display: inline;
}
label {
    cursor: default;
}
.normal {
    font-weight: normal;
}
.inlineimg {
    vertical-align: middle;
}
.underline {
    text-decoration: underline;
}
.boplink a {
    border-bottom: 1px dotted #484848;
    text-decoration: none;
    color: #484848;
}
/* ***** navbar table border ***** */

