css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(clockingit.com) {
    /* your code goes here */
    .file_cell center {
        float: left;
    }
    .file_cell div {
        float: right;
    }
    .file_cell_img,
    .file_cell_icon,
    .file_cell {
        width: auto !important;
        height: auto !important;
    }
    #flash_body {
        display: none;
    }
    body {
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
        font-size: 12px;
    }
    a {
        color: #337ab7;
        text-decoration: none;
    }
    a {
        background-color: transparent;
    }
    #globalMenu {
        background-image: none;
        border-left: none;
        display: block;
        list-style-type: disc;
        -webkit-margin-before: 1em;
        -webkit-margin-after: 1em;
        -webkit-margin-start: 0px;
        -webkit-margin-end: 0px;
    }
    #tabmenu {
        border-bottom: 1px solid #ddd;
    }
    #tabmenu>ul>li>a {
        padding: 10px 15px;
        border-bottom: 1px solid #ddd;
    }
    #tabmenu li a,
    #tabmenu #search_menu {
        padding: 10px 15px;
        border-top: none;
        border-right: none;
        border-left: none;
        border-bottom: none;
        height:20px;
    }
    #tabmenu li
    {
        height:20px;
    }
    #tabmenu li ul {
        margin-top: 25px;
        margin-left: 0px;
    }

    #tabmenu>ul>li>ul>li{
        min-width:150px;
    }

    #tabmenu li ul a {
        padding-top:10px;
        padding-bottom:5px;
    }

    #tabmenu li.active>a {
        cursor: default;
    }

    #tabmenu li.active a {
        background-color: #fff;
        background-image: none;
        border: none;
        border-bottom-color: transparent;
        padding: 10px 15px;
    }
    .task-name {
        border-left-color: #1b809e;
        padding: 10px;
        border: 1px solid #eee!important;
        border-left-width: 5px;
        border-radius: 3px;
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
        font-size: 12px;
    }
    .in_progress {
        color: #aa6708;
        font-style: normal;
    }
}
