css:
@-moz-document domain(polishsource.cz) {
body {
	font-family: "tahoma", "arial", "helvetica", "sans-serif";
	font-size: 8pt;
background-color: #001437;
  color: #000000;
  margin-top: 0px;
  margin-bottom: 0px;
}

.superpasek {
  border-collapse: collapse;
  border: none;
   background-color: #A3B3CA; 
}

.titlebar {
  border-collapse: collapse;
  border: none;
   background-color: #A3B3CA; 
}

titlebar {
  border-collapse: collapse;
  border: none;
   background-color: #A3B3CA; 
}

table {
  border-collapse: collapse;
  border: none;
   background-color: #A3B3CA; 
}

table.main, table.mainouter {
  background-color: #547295;
}

table.bottom {
  background: transparent;
}

h1 {
  font-size: 12pt;
}
w1 {
display: block;
}
w2,w3,w4 {
display: none;
}
h2 {
  font-size: 10pt;
  margin-bottom: 6px;
}

p {
	font-size: 8pt;
}

p.sub { 
  margin-bottom: 4pt;
}

td {
	font-size: 8pt;
	border: solid #000000 1px;
}

td.normal {
        font-size: 8pt;
        border: solid #000000 0px;
}

td.embedded {
   border: none;
   padding: 0px;
  text-align: left;
}

td.bottom {
  border: none;
}

td.heading {
  font-weight: bold;
}

td.text {
  padding: 10pt;
  text-align: left;
}

td.comment {
  padding: 10pt;
  font-size: 8pt;
  text-align: left;
}

td.colhead {
  font-weight: bold;
  color: #ffffff;
  background-color: #0055A4;
}






/********************************************************************/
th.colhead {
  font-weight: bold;
  color: #ffffff;
  background-color: #0055A4;
  font-size: 8pt;
  border: 1px;
  border-style: solid;
  border-color: black;
  text-align: center;
}

th.currency {
  font-weight: bold;
  color: #ffffff;
  background-color: #0055A4;
  font-size: 8pt;
  border: 1px;
  border-style: solid;
  border-color: black;
  text-align: center;
}

th.number {
  font-weight: bold;
  color: #ffffff;
  background-color: #0055A4;
  font-size: 8pt;
  border: 1px;
  border-style: solid;
  border-color: black;
  text-align: center;
}

th.nosort {
  font-weight: bold;
  color: #ffffff;
  background-color: #0055A4;
  font-size: 8pt;
  border: 1px;
  border-style: solid;
  border-color: black;
  text-align: left;
}
/********************************************************************/







td.rowhead {
  font-weight: bold;
  text-align: right;
  vertical-align: top;
}

td.title {
	font-size: 14pt;
}

td.navigation {
	font-weight: bold;
	font-size: 10pt;
   border: none;
}

td.clear {
  padding: 0px;
  border: 0px;
  background: transparent;
}

form {
	margin-top: 0;
	margin-bottom: 0;
}

.sublink {
	font-style: italic;
	font-size: 7pt;
	font-weight: normal;
}

font.gray
{
  color: #ACA899;
  text-decoration: underline;
}

a:link, a:visited {
  text-decoration: underline;
  color: #000000;
  /*font-weight: bold;*/
}

a:hover {
  color: #F0F8FF;
}

a.index {
	font-weight: bold;
}

a.biglink {
	font-weight: bold;
	font-size: 12pt;
}

a.altlink:link, a.altlink:visited{
	font-weight: bold;
	color: #A83838;
	text-decoration: none;
}

a.altlink:hover {
  text-decoration: underline;
}

.important {
	font-weight: bold;
	font-size: 8pt;
}

.red {
	color: #ee0000;
}

.yellow {
	color: #997700;
}

.green {
	color: #000000;
}

input, select, textarea {
	font-family: "tahoma", "arial", "helvetica", "sans-serif";
	font-size: 8pt;
}

.btn {
  width: 75px;
  height: 23px;
}

.small {
  font-size: 7pt;
}
.wstaw {
  color: yellow;
  text-decoration: none;
}
.pomoc {
  color: white;
  text-decoration: none;
}
.wsparcie {
  font-size: 10;
  color: silver;
}
.footer {
  font-size: 10;
  color: #547295;
  text-decoration: none;
}
.datki {
  font-size: 12;
  color: yellow; 
  text-decoration: none;   
}

.support {
  font-size: 85%;
  color: #B0C4DE;
  text-decoration: none;
}

a.support:hover {
  text-decoration: bold;
}

.big {
  font-size: 10pt;
}

li {
  margin-top: 6pt;
  margin-bottom: 6pt;
}

ul {
  margin-left: 16pt;
  margin-top: 0px;
  margin-bottom: 0px;
}

img.border  
{  
  border-style: solid;  
  border-color: #000000; 
  border-width: 1px; 
}

table.message {
 border: solid #000000 1px;
}

table.ncode_imageresizer_warning {
    background: #FFFFE1;
    color: #000000;
    border: 1px solid #CCC;
    cursor: pointer;
}

table.ncode_imageresizer_warning td {
    font-size: 8pt;
    color: red;
    vertical-align: middle;
    text-decoration: bold;
}

table.ncode_imageresizer_warning td.td1 {
    padding: 5px;
}

table.ncode_imageresizer_warning td.td1 {
    padding: 2px;
}

#line2 table td {

border: none;

}


#line2 td {

border: 0px solid #fff;

}

#line table td {

border: none;

}



#line td {

border: 0px solid #ffffff;

}

}
