  background: url("https://i.imgsafe.org/da5a795.jpg") no-repeat scroll center top;
  height: 200px;
  margin: 0 auto;
  width: 100%;
}
#searchbars input {
  background-color: black;
  border: 1px solid #ccc;
  color: white;
  font-size: 1em;
  padding: 2px 2px 2px 4px;
  position: relative;
}
div#wrapper div#header a div#logo{
    background-color: #263238;
}
.filter_torrents {
  margin: auto;
  width: 100%;
}
    strong.torrent_label.tooltip.tl_notice{color: green!important;} 
/* 
torrent 
*/
div#wrapper div#content div.thin.widethin table#torrent_table.torrent_table.cats.grouping tbody tr.colhead{
