/*--------------------------------------------------------------------------------
  Search Engine Tagmarks (Version 0.1.5)
  --------------------------------------------------------------------------------
  Author: Louis H - Tested on: Windows 7 Firefox 3.5.3 - Last Update: 21.09.2009
  --------------------------------------------------------------------------------
  Description:
    Add Tagmark on the searchbar and search menu when match keyworkswith search engines' name
  --------------------------------------------------------------------------------*/

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);



.searchbar-textbox > .search-go-container > .search-go-button {

/*
 ======== change the default search button, tagmark positions if not match with your style =======
*/

/*list-style-image: url(chrome://browser/skin/Search-glass.png) !important;*/

background-repeat: no-repeat !important;
background-position: 0px 2px !important;
-moz-padding-start: 6px !important;
} 

.searchbar-popup > menuitem > label {
background-repeat: no-repeat !important;
background-position: right center !important;
-moz-padding-start: 6px !important;
}


/*
 ======================== Format ===================================================

-------------- equal types ----------------
      =  : equal
      *= : have this word
      ^= : begin with
      ~= : end with
-------------------------------------------
*/


.searchbar-textbox[emptytext = "##KEYWORD##"] > .search-go-container > .search-go-button 
{
/*------------------ Paste below --*/ background-image: url("data:image/png;base64,/*########################################################               Your base64 source data                 ########################################################*/")!important; /*-- Paste upward -------------------------------------------------*/
}



