css:
@-moz-document domain("thepiratebay.se"), domain("thepiratebay.org") {
    html,body{
        margin: 0;
        padding: 0;
    }
    body{
        font-size: 11px;
        font-size: 0.71em;
        min-width: 800px;
        line-height: initial;
    }
    
    select, input{
        border: 1px solid #ccc;
        border-radius: 3px;
    }
    
    #fp h1 a {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 265px !important;
    padding-top: 295px !important;
    background: url(http://i.imgur.com/HTpVsSS.png) no-repeat !important;}
    
    #TPBlogo {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 78px !important;
    padding-top: 87px !important;
    background: url(http://i.imgur.com/Gxdj9Xi.png) no-repeat !important;}
    
    #fp h1 a {
        margin-bottom: 50px;
    }
    
    #searchResult center:nth-child(1) {
        color: black;
    }
    
    a, a:hover, a:visited, a:focus, a:link, #browseContainer a{
        text-decoration: none;
        border: none;
        color: #0F0F0F;
        transition: 0.2s linear color;
    }
    a:hover, #browseContainer a:hover, table#searchResult a:hover, dt#upload a:hover {
        color: #0F0F0F;
        border: none;
        text-decoration: none;
    }
    
    #fp{
        width: 95%;
    }
    #fp #navlinks a {
       font-size: 1.1em;
    }

    #fp #inp input {
       padding: 10px;
       width: 500px;
        border: 1px solid #ccc;
        border-radius: 3px;
    }
    #fp #inp span {
       line-height: 1.5em;
       font-size: 0.9em;
    }
    
    #subm input, .submitbutton, #registerTable input[type="submit"], #loginForm input[type="submit"]{
        border: 1px solid #ccc;
        border-radius: 3px;
        padding: 5px;
        transition: 0.1s linear background, 0.1s linear color, 0.1s linear border;
        background: #eee;
        cursor: pointer;
    }
    #subm input:hover, .submitbutton:hover, #registerTable input[type="submit"]:hover, #loginForm input[type="submit"]:hover{
        border: 1px solid #614226;
        color: #eee;
        background: #0F0F0F;
    }
    
    footer{
        margin-top: 30px;
    }
    footer nav a, #foot a{
        padding: 3px 5px;
        font-size: 1.1em;
    }
    #howdl a {
        display: none;
    }
    .uldetails tbody:nth-child(1), tr:nth-child(1), td:nth-child(1), label:nth-child(1) {
        color: black;
    }
    .uldetails tr:nth-child(7) {
        display: none;
    }
    .uldetails tr:nth-child(6) {
        display: none;
    }
    .uldetails tr:nth-child(5) {
        display: none;
    }
    #delete {
        display: none;
    }
    #desc {
        border-top-color: black;
        border-left-color: black;
        border-right-color: black;
        border-bottom-color: black;
    }
    .nfo {
        border-top-color: black;
        border-left-color: black;
        border-right-color: black;
        border-bottom-color: black;
    }
    #torrent {
        color: black;
    }
    
    /*table*/
    #content {
       min-width: 800px;
        margin: 0 auto;
    }
    #main-content{
        margin: 40px 0px;
    }
    
    table#searchResult tr, table#searchResult tr.alt {
       background: #eee;
    }
    table#searchResult tr:hover, table#searchResult tr.alt:hover  {
       background: #E1E1E1;
    }
    table#searchResult tr td{
        padding: 6px 3px;
    }
    table#searchResult tr td:nth-child(3), table#searchResult tr td:nth-child(4){
        text-align: left;
    }
    
    table#searchResult th, table#searchResult td {
       padding: 8px 10px;
    }
    
    thead#tableHead th, thead#tableHead th a, thead#tableHead th a:hover{
        color: white;
        background: #0F0F0F;
    }
    .detLink{
        font-weight: bold;
    }
table#searchResult, h2{
    text-align: center !important;
    width: 70% !important;
    margin: auto !important;
}
    #q {
    text-align: unset !important;
    width: 670px !important;
}

    /*header*/
    
    table#searchResult {
        text-align: left;
        width: 100%;
    }
    .header th:nth-child(2), th:nth-child(4), th:nth-child(5) {
        width: 0px;
    }
    #header{
        padding: 15px 0;
        margin: auto;
    }
    #q{
        margin: auto;
        width: 900px;
        font-size: 1.1em;
    }
    #q .inputbox{
        border: 1px solid #ccc;
        border-radius: 3px;
        padding: 5px 7px;
        margin: 10px 10px 10px 0;
        width: 450px;
    }
    .submitbutton{
        padding: 4px 8px;
    }
    #TPBlogo{
        margin-right: 20px;
    }
    
    h2 {
       font-size: 1.1em;
       line-height: 1.5em;
       text-align: center;
       padding: 10px;
       border-bottom: none;
       color: white;
       background: black;
    }
    h2 span{
        float: none;
        margin-right: 15px;
    }
    h2 a{
        color: #eee !important;
    }
    
   /*torrent*/
    
    #detailsouterframe {
       min-width: 800px;
       text-align: right;
    }
    #details{
        background: #E1E1E1;
    }
    #detailsframe #title {
       padding: 10px;
        color: white;
        background: #0F0F0F;
        text-align: center;
    }
    #details dt {
       color: black;
    }
    #details dl {
       margin: 10px 20px 10px 0px;
    }
    
    #details dd, #details dt {
       border-bottom: none;
    }
    
    #details dl.col1 {
       width: 370px;
    }
    #details dl.col2 {
       width: 370px;
    }
    #details dl.col2 dd, #details dl.col1 dd {
        margin: 0 0 10px 0px;
    }
    #details br { 
        display: none;
    }
    .download {
        font-size: 1.1em;
    }
    
    .nfo {
       margin: 20px 0;
       width: 760px;
       padding: 10px;
    }
    #details pre {
        font-size: 14px;
        line-height: 1.0em;
    }
    #commentsheader h4 {
        color: black;
    }
    #comments p.byline {
        color: black;
    }
    
    #details #CommentDiv {
        background-color: #E1E1E1;
    }
    
    p.info textarea {
        width: 730px;
        margin: 0px 0;
    }
    
    /* language */
    tbody tr td center{
        margin: 3px;
    }
    table[dir="ltr"]{
        width: 800px;
        margin: 40px 0;
    }
    
    
    /*category*/
    
    #categoriesTable {
       width: 1000px;
       min-width: 800px;
       margin: 20px auto;
    }
    #browseContainer dt {
        background: #eee;
        padding: 7px 10px;
    }
    #browseContainer dd {
       margin: 0px 0px 35px 10px;
    }
    dt#showall a, dt#showall a:hover, dt#latest, dt#latest a:hover{
       border-bottom: none;
    }
    
    #browseContainer .categoriesContainer {
       width: 33%;
    }
    
    #browseContainer dt {
        font-size: 1.3em;
    }
    
    
    /*about*/
    #about, .policy, #searchcloud{
        width: 800px !important;
        margin: 40px auto;
    }
    
    
    /*comments*/
    #commentsheader{
        font-size: 1.2em;
    }
    #comments{
        font-size: 1.0em;
    }
    #comments > div{
        margin: 20px 0;
    }
    #comments p{
        margin-left: 0;
    }
    .comment{
        padding: 10px;
        border-radius: 3px;
    }
    
    
    /*blog*/
    #blog{
        width: 800px;
    }
    .post h3, .doodle h3{
        background: #eee;
        margin: 0 0 10px 0;
        padding: 10px;
    }
    .doodle{
        margin: 0 0 60px 0;
        border: none;
    }
    
    /*register*/
    #registerTable {
       width: 500px;
        margin: 40px auto;
        line-height: initial;
    }
    #registerTable input{
       width: 250px;
        padding: 5px 10px;
        margin: 5px 0;
    }
    #registerTable input[type="submit"]{
       width: 150px;
    }
    
    #recaptcha_area{
        margin: 20px auto 0 auto;
    }
    
    /*login*/
    
    #loginForm {
       width: 370px;
       margin: 40px auto;
    }
    #loginForm input[type="submit"]{
        width: 150px;
        position: initial;
        display: block;
        margin: auto;
    }
    #loginForm label {
       display: block;
       margin-top: 0;
        line-height: 2em;
        height: 40px;
    }
    #loginForm input {
        width: 200px;
        padding: 5px 10px;
    }
   
    /*ads*/
    
    #sky-right, #sky-center, #sky-banner, #foot iframe, .ads, iframe[src*="adbrau"]{
        display: none !important;
    }
    .download a:nth-child(2), #howdl, #icons, #fbanners, .uldetails > tbody:nth-child(1) tr:nth-child(4){
        display: none;
    }
    
        #fp h1 a {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 265px !important;
    padding-top: 295px !important;
    background: url(http://i.imgur.com/vvb2p89.png) no-repeat !important;
        }
    
    #TPBlogo {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 78px !important;
    padding-top: 87px !important;
    background: url(http://i.imgur.com/bPwB2H2.png) no-repeat !important;
    }
}
