css:
@-moz-document domain("vidzi.tv") {
/*GENERAL*/html,body,div#container {background: #141414 !important;}/*HEADER*/#container>header {background: #141414 !important; border-bottom: 1px solid transparent !important;} h1,h2,h3,h4,h5,h6 {color: #D3D3D3;  font-family: Helvetica Neue,Helvetica,sans-serif !important; font-weight: 100 !important; transition: all .3s linear;} h2.video-title:hover {background: black; -webkit-box-shadow: 0px 0px 23px 10px rgba(0,0,0,0.75);-moz-box-shadow: 0px 0px 23px 10px rgba(0,0,0,0.75);box-shadow: 0px 0px 23px 10px rgba(0,0,0,0.75);} img{opacity: .6;}header a {color: #999 !important;}/*PLAYER*/#vplayer_wrapper {width: 1280px !important;height: 720px !important;-webkit-box-shadow: 0px 0px 23px 10px rgba(0,0,0,0.75);-moz-box-shadow: 0px 0px 23px 10px rgba(0,0,0,0.75);box-shadow: 0px 0px 23px 10px rgba(0,0,0,0.75);}#main > .row,#hero > .row,header > .row,footer > .row {width: 1280px !important;}div#video-wrap {height: 720px !important;}/*FOOTER*/#container>footer{display: none !important;} #video-meta, #video-links{display: none !important;}body>div:nth-child(2) { display: none !important; } /*MEDIA QUERIES*/@media screen and (min-width: 1930px) {/*SCREENS OVER 1080p*//*PLAYER*/#vplayer_wrapper {width: 1920px !important;height: 1080px !important;}#main > .row,#hero > .row,header > .row,footer > .row {width: 1920px !important;}div#video-wrap {height: 1080px !important;}}@media screen and (max-width: 1281px) {/*FOR 720p SCREENS*//*PLAYER*/#vplayer_wrapper {width: 853px !important;height: 480px !important;}#main > .row,#hero > .row,header > .row,footer > .row {width: 853px !important;}div#video-wrap {height: 480px !important;}}@media screen and (max-width: 1025px) {/*SCREENS UNDER 720p*//*PLAYER*/#vplayer_wrapper {width: 640px !important;height: 360px !important;} body{overflow-x: hidden;} #main > .row,#hero > .row,header > .row,footer > .row {width: 640px !important;}div#video-wrap {height: 360px !important;}}
}
