  /*////////////////////
  /  GENERAL SNIPPETS  /
  ////////////////////*/

  a {
    transition: 1s!important;
    text-decoration: none!important;
    font-weight: 700;
  }
  
  a:hover, a:focus {
    color: #c05c77!important;
  }
  
  .ugc a {
    color: #4c9db9!important;
    transition: 1s;
  }
  
  .ugc a:hover, .ugc a:focus {
    background: none!important;
    color: #c05c77!important;
    -webkit-box-shadow: none!important;
    -khtml-box-shadow: none!important; 
    box-shadow: none!important;
  }
  
  .adsbygoogle.bannerAd {
    background: #e9e9e9;
    padding: 10px;
    border: 1px solid #d9d9d9;
    margin: 10px;
  }
  
  /*/////////////
  /  MAIN PAGE  /
  /////////////*/
  
  /* Moves menu to top */
  
  #navigation {
    position: absolute;
    top: 0;
    width: 100%;
    z-index: 999!important;
  }
  
  #searchBar {
    position: absolute;
    top: 92px;
    right: 20px;
  }
  
  /* Styles menu area */
  
  .navTabs {
    background: #393939;
    color:#fff;
  }
  
  .navTabs .navLink {
    font-family: "Oswald"!important;
    font-size: 20px;
    font-weight: 400!important;
    letter-spacing: 0.5px;
    text-transform: uppercase;
    color: #fff;
  }
  
  .navTabs .navTab {
    transition: 1s!important;
  }
  
  .navTab.PopupOpen a, .navTab.PopupOpen a:focus, .navTab.PopupOpen a:active, .navTab.unread a i {
    color: #c05c77!important;
  }
  
  .navTabs .navLink .itemCount {
    margin-left: -5px!important;
    background: #c05c77!important;
  }
  
  .navTabs .navLink .itemCount .arrow {
    border-top-color: #c05c77!important;
  }
  
  .navTab.selected a, .tabLinks {
    background: #5b5b5b!important;
    border:none!important;
  }
  
  .navTab .tabLinks a {
    font-size: 14px!important;
  }
  
  #navigation:after {
    content: " ";
    display:block;
    position: relative;
    width: 100%;
    bottom:1px;
    height:20px;
    background: linear-gradient(-45deg, transparent 75%, #5b5b5b 75%) 0 50%,
                linear-gradient( 45deg, transparent 75%, #5b5b5b 75%) 0 50%;
    background-repeat: repeat-x;
    background-size:20px 20px, 20px 20px;
  }
  
  /* Styles dropdown menus */
  
  .Menu {
    -webkit-box-shadow: none;
    -khtml-box-shadow: none;
    box-shadow: none;
    border-width: 5px 0 0 0;
    margin-top: -4px;
    font-size: 13px;
  }
  
  .Menu:after {
    content: " ";
    display:block;
    position: relative;
    width: 100%;
    bottom:1px;
    height:20px;
    background: linear-gradient(-45deg, transparent 75%, #fff 75%) 0 50%,
                linear-gradient( 45deg, transparent 75%, #fff 75%) 0 50%;
    background-repeat: repeat-x;
    background-size:20px 20px, 20px 20px;
  }
  
  .Menu .primaryContent {
    background: #393939;
    color: #fff;
  }
  
  .Menu .primaryContent h3 a, .Menu .primaryContent h3 {
    font-size: 18px;
  }
  
  .Menu .primaryContent a:hover {
    text-decoration: none!important;
  }
  
  .Menu .secondaryContent {
    padding-top: 5px!important;
    padding-bottom: 10px!important;
    color: #393939;
  }
  
  .Menu a {
    color: #4c9db9;
    font-size: 14px;
  }
  
  .Menu .muted {
    color: #e6e6e6;
    font-style: italic;
  }
  
  .Menu label {
    font-size: 11px;
    transition: 1s;
  }
  
  .Menu label:hover, .Menu label:focus {
    color:#393939;
  }
  
  .Menu .blockLinksList {
    padding: 0;
  }
  
  .navTab .fa-envelope, .navTab .fa-map-marker {
    font-size: 20px;
  }
  
  #AccountMenu .blockLinksList {
    width: 100%;
  }
  
  .Menu .blockLinksList li {
    border-top: 1px solid #e8e8e8;
  }
  
  #AccountMenu.Menu .blockLinksList li a {
    width: 100%!important;
  }
  
  .Menu .sectionFooter {
    background: #fff;
  }
  
  .Menu .sectionFooter a {
    font-size: 13px;
  }
  
  .Menu .sectionFooter textarea {
    padding: 10px 5px;
    background: #393939;
    color: #e9e9e9;
    border-radius: 5px;
  }
  
  .Menu .listItemText .muted {
    color: #6e6e6e;
  }
  
  .Menu .Alert {
    transition: 1s;
  }
  
  /* Avatar styling */
  
  .avatar img, .avatar .img {
