  }
  
  .PanelScroller.Notices .navContainer a span {
    border-top-color: #fff!important;
  }
   
  /* Styles Prefixes */
  
  .prefix {
    font-family: "Oswald";
    font-size: 16px!important;
    font-weight: 300!important;
    text-transform: uppercase;
    color: #fff!important;
    padding: 3px 7px 5px 6px!important;
  }
  
  .prefixLightGreen {
    background: #69aa1b!important;
    border: none;
  }
  
  .prefixOrange {
    background: #e87d0a!important;
    border: none;
  }
  
  /* Styles News Widget */
  
  #widget-24>h3:nth-child(1) {
    font-family: "Oswald";
    font-size: 20px;
    font-weight: 300;
    text-transform: uppercase;
    letter-spacing: 1px;
    color: #393939;
