    border-bottom: 3px solid #393939;
    padding: 1px 10px;
    margin-bottom: 4px;
  }
  
  #widget-24 .discussionListItem {
    border-bottom: none!important;
    display: inline-block;
    width: 48%;
    padding: 0!important;
  }
  
