      text-transform: uppercase;
      color: #393939!important;
  }
  
  #taigachat_full .categoryStrip .nodeDescription {
    color: #393939!important;
  }
  
  #taigachat_full .categoryStrip .nodeTitle:before {
    display: none;
  }
  
  #taigachat_box {
    border-radius: 10px!important;
  }
  
  #taigachat_box li, #taigachat_box .username {
    font-size: 14px!important;
  }
  
  #taigachat_message {
    background: #efefef;
    color: #393939;
    padding: 10px;
    border: none!important;
    border-radius: 5px;
  }
  
  #taigachat_controls .button {
    background: none!important;
  }
  
  #taigachat_colorpicker {
    padding-top: 6px;
  }

  #taigachat_box .username .style2 {
  	color: #393939!important;
  }
   
  /* Displays two forums per row */
  
  #forums .node.forum, #forums .node.page {
