/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("youtube.com"),
               domain("youtube.com.nyud.net") {


#masthead .bar {background-color:#3a3939 !important;}



/* Dark Grey Carbon background */
body {
	background-color: #000000!important;
}

/* Header and Comment Bars*/
.headerBox, .headerBoxOpacity, .watch-more-action-share { background-color: #3a3939 !important; border-color: #222 !important; }
.headerRCBox {  background: #fff !important; border-color: #3a3939 !important; }
#header-left { background : #3e3e3a !important; width: 100px !important; height : 20px !important; text-align: center !important; margin-left: 7px !important; border: 1px solid #424242 !important; padding-top: 0 !important; }
#header-middle { background : #3e3e3a !important; width: 450px !important; height : 20px !important; text-align: center !important; margin-left: 9px !important; border: 1px solid #424242 !important; padding-top: 0 !important; }

/* Common to All links */
a:link { color: #82BDFF !important; text-decoration: none !important; }
a:visited { color: #33FFFF !important; }
a:hover { color: #ff6600 !important; }
:focus { outline: none; }
.eLink { border-bottom: 1px solid #666 !important; }

/* Text colours */
span, div, p, li, td { color: #acabab !important; }
input *, textarea * { color: -moz-fieldtext !important; }
.tooltip-wrapper-box * { color: #000 !important; }
.aAutoComplete .cAutoComplete, .google-ac-a .google-ac-c { background: #fff7df; color: #008000 !important; }
.aAutoComplete .dAutoComplete, .google-ac-a .google-ac-d { background: #fff7df; color: #696969 !important; }
.google-ac-e td, .google-ac-e td span { background: #fff7df; color: #8a2be2 !important; }





/* Input Box */
input { -moz-appearance: none; font:11px Tahoma,sans-serif; padding:.2em .4em; background:#fff7df; border:1px solid #cccccc; color:#008000; }
input[type="submit"], input[type="button"] { background:#f4c100; border:none; color:Highlight; cursor:pointer; padding:.2em .6em; }
input[type="submit"]:hover, input[type="button"]:hover { color: #ff6600; }
table.message-display th, table.message-display td, table.message-display div {background:#333!important}

/* Select input Box */
select { -moz-appearance: none; font: 11px Tahoma,sans-serif; padding: .2em .4em; background: #fff7df; border: 1px solid #cccccc; color: #008000; }
select:hover { color: #ff6600; }

/* Text headers */
h1, h2, h3, h4, h5, h6, .hpBlockHeadingGray, .name, .title, #vidTitle, .wsHeading, .header, #catIndexGridHeading, #footer .links th {
	color: #ddd!important;
}

/* remove "Videos being watched right now" */
#active_sharing { display: none !important; }

/* Channels masthead*/
.channelMastheadTable, #mastheadTable { background: #3a3939 !important; padding-bottom: 10px !important; -moz-border-radius-bottomleft: 9px !important; -moz-border-radius-bottomright: 9px !important; }
#mastheadBottom, img[src="/img/masthead_round.gif"], #smallMastheadBottom { display: none !important; }

/* Purple and Yellow blocks */
.homepage-yellow-block, .purpleBlock.alignC { background: #3a3939 !important; padding: 8px 10px !important; border: 1px solid #424242 !important; -moz-border-radius: 10px !important; }
.homepage-yellow-block-bot, .purpleBlockBot { display: none !important; }

/* button/action - text colour */
#button-signin-title, .action-button-text, .edit-button-text, .title { color: Highlight !important; text-decoration: none !important; }
.watch-action-text { text-decoration: none !important; }
