css:
@-moz-document url-prefix("https://meh.com/forum"), url-prefix("https://meh.com/polls"), url-prefix("http://meh.com/forum"), url-prefix("http://meh.com/polls") {
body {
	background-color: hsl(263, 47%, 24%);
}
a,
a:hover,
a:link,
a:active,
a:visited,
.votes,
#header.dark .category ul,
#header.dark .sort ul,
#header.dark .category ul a li,
#header.dark .category ul a li,
#header.dark .sort ul a li,
#header.light .category ul,
#header.light .sort ul,
#header.light .category ul a li,
#header.light .category ul a li,
#header.light .sort ul a li {
	-webkit-transition: opacity 0.5s ease 0s, color 0.5s ease 0s;
	transition: opacity 0.5s ease 0s, color 0.5s ease 0s;
}
ul.comments .comment .show-replies span,
.e-content a,
.e-content a:link {
	color: hsl(263, 100%, 90%)
}
.e-content a:visited,
.e-content a:active {
	color: hsl(263, 35%, 50%)
}
.topics .topic .p-name a:hover,
.e-content a:hover {
	color: hsl(44, 100%, 76%);
}
#header.dark::before,
#header.light::before {
	background-color: hsla(263, 56%, 30%, 0.88);
}
#header nav a.logo {
	background-position: -4px -7px;
}
#header.dark nav .breadcrumbs li a,
#header.light nav .breadcrumbs li a,
#header nav ul.links li a,
#header.dark .forum-actions ul li a,
#header.light .forum-actions ul li a {
	color: hsl(263, 0%, 100%);
}
#header.dark .category ul a li.active,
#header.dark .category ul a li.active,
#header.dark .sort ul a li.active,
#header.dark .category ul a:hover li,
#header.dark .sort ul a:hover li,
#header.light .category ul a li.active,
#header.light .category ul a li.active,
#header.light .sort ul a li.active,
#header.light .category ul a:hover li,
#header.light .sort ul a:hover li {
	color: hsl(263, 0%, 100%);
	border-color: hsl(263, 0%, 100%)
}
#header.dark .category ul a li,
#header.dark .sort ul a li,
#header.light .category ul a li,
#header.light .sort ul a li {
	color: hsla(263, 0%, 100%, 0.5);
}
#header.dark .category ul,
#header.dark .sort ul,
#header.light .category ul,
#header.light .sort ul {
	border-color: hsla(263, 0%, 100%, 0.5);
	opacity: 0.45;
}
#header nav ul.links li.whatismeh a i {
	background-position: -578px 0px;
}
#header nav ul.links li.account a.authenticated i {
	background-position: -288px 0px;
}
#header nav ul.links li.vmp a i {
	background-position: -240px 0px;
}
#header nav ul.links li.past-deals a i {
	background-position: -624px 0px;
}
#header nav ul.links li.theme-songs > a i {
	background-position: -530px 0px;
}
#header nav ul.links li.forum a i {
	background-position: -480px 0px;
}
.show-comments {
	background-color: hsl(263, 40%, 50%);
	color: hsl(263, 100%, 85%);
}
body,
.poll ul li .base,
.poll ul li .graph,
.topics .topic .p-name a,
.topic .p-name,
.topic .e-content,
ul.comments .comment .show-replies,
ul.comments .comment .e-content,
ul.comments .comment .replies > .reply .e-content {
	color: hsl(263, 100%, 80%);
}
.poll ul li .graph {
	background-color: hsl(263, 47%, 34%);
}
.topics .topic .created-by,
.topics .topic a,
.topic .created,
.topic .created a,
ul.comments .comment .created,
ul.comments .comment .created a,
.topic .activity,
.actions li a {
	color: hsl(263, 60%, 60%);
}
.topics .topic a:hover,
.topic .created a:hover,
ul.comments .comment .created a:hover,
.actions li a:hover {
	color: hsl(44, 100%, 69%);
}
.topics .topic .p-name a:hover,
ul.comments .comment .show-replies span:hover,
.e-content a:hover {
	color: hsl(44, 100%, 76%);
}
#header nav a,
#header nav ul.links li a,
#header.dark .forum-actions ul li a,
#header.light .forum-actions ul li a {
	opacity: 0.45;
}
.votes {
	color: #FFFFFF;
	opacity: 0.5;
}
#header nav a:hover,
#header nav ul.links li a:hover,
#header.dark .forum-actions ul li a:hover,
#header.light .forum-actions ul li a:hover,
.votes:hover,
.votes.for,
#header.dark .category ul:hover,
#header.dark .sort ul:hover,
#header.light .category ul:hover,
#header.light .sort ul:hover {
	opacity: 1;
}
#header nav {
	border-bottom: 2px solid rgba(255, 255, 255, 0.22);
}
.e-content blockquote,
.e-content code,
.e-content pre {
	background-color: hsl(263, 47%, 19%);
}
.e-content blockquote,
ul.comments .comment .replies>.reply.unread:before {
	border-color: hsl(263, 100%, 85%);
}
.editor textarea,
#add-comment-mobile-footer textarea,
.dialog.mobile textarea,
.image-uploader .image-selector span input {
	background-color: hsl(263, 47%, 19%);
	color: hsl(263, 100%, 80%);
}
.editor textarea::-webkit-input-placeholder,
#add-comment-mobile-footer textarea::-webkit-input-placeholder,
.dialog.mobile textarea::-webkit-input-placeholder,
.image-uploader .image-selector span input::-webkit-input-placeholder {
	color: hsla(263, 100%, 80%, 0.5);
}
.editor textarea:-moz-placeholder,
#add-comment-mobile-footer textarea:-moz-placeholder,
.dialog.mobile textarea:-moz-placeholder,
.image-uploader .image-selector span input:-moz-placeholder {
	color: hsla(263, 100%, 80%, 0.5);
}
.editor textarea::-moz-placeholder,
#add-comment-mobile-footer textarea::-moz-placeholder,
.dialog.mobile textarea::-moz-placeholder,
.image-uploader .image-selector span input::-moz-placeholder {
	color: hsla(263, 100%, 80%, 0.5);
}
.editor textarea:-ms-input-placeholder,
#add-comment-mobile-footer textarea:-ms-input-placeholder,
.dialog.mobile textarea:-ms-input-placeholder,
.image-uploader .image-selector span input:ms-input-placeholder {
	color: hsla(263, 100%, 80%, 0.5);
}
.editor,
#add-comment-mobile-footer textarea,
.dialog.mobile textarea {
	border-color: rgba(0, 0, 0, 0.5);
}
.editor .buttons {
	background-color: hsl(263, 47%, 17%);
	color: #FFF;
}
.editor .buttons button,
#add-comment-mobile-footer footer button,
.dialog.mobile header button,
.image-uploader .okcancel button {
	background-color: hsl(263, 47%, 29%);
	border-color: hsl(263, 47%, 34%);
	color: hsl(263, 100%, 80%);
	text-shadow: 0px 1px hsl(263, 47%, 14%);
}
.wmd-spacer {
	background-color: hsl(263, 47%, 30%);
}
.wmd-button>span {
	background-image: url('https://i.imgur.com/AZnPg8g.png');
}
.image-uploader-wrapper {
	padding: 18px;
	box-sizing: border-box;
	background-color: hsl(263, 47%, 24%);
	height: auto;
	min-height: 292px;
	margin-top: -166px;
}
.image-uploader #uploader-button {
	float: none;
	background-color: hsla(263, 47%, 29%, 0);
	border-color: hsla(263, 47%, 34%, 0);
	color: hsla(263, 100%, 80%, 0);
	text-shadow: 0px 1px hsla(263, 47%, 14%, 0);
	margin: 30px;
	overflow: hidden;
}
.image-uploader #uploader-button::after {
	content: "BROWSE YOUR DEVICE";
	background-color: hsl(263, 47%, 29%);
	border-color: hsl(263, 47%, 34%);
	color: hsla(263, 100%, 80%, 1);
	text-shadow: 0px 1px hsla(263, 47%, 14%, 1);
	position: absolute;
	width: 100%;
	text-align: center;
	left: 0;
	right: 0;
	top: 0;
	bottom: 0;
	z-index: -1;
}
.image-uploader .image-selector .or {
	float: none;
	text-transform: uppercase;
	margin-top: 90px;
}
.image-uploader .drag-instructions {
	margin: 0px;
	border: 1px solid hsl(263, 47%, 34%);
	text-align: center;
	text-transform: uppercase;
	padding: 100px 10px 30px;
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	border-radius: 10px;
	z-index: -1;
}
.image-uploader .okcancel {
	position: relative;
	text-align: center;
	margin-top: 20px;
}
.image-uploader .okcancel button,
.image-uploader .okcancel a {
	float: none;
}
.image-uploader .okcancel a {
	position: absolute;
	right: 10px;
	opacity: 0.5;
}
.dialog.mobile header a {
	color: hsl(263, 60%, 60%);
}
.image-uploader .okcancel a:hover {
	opacity: 1;
}
.dialog.mobile {
	background-color: hsl(263, 47%, 24%);
	padding: 10px;
	border-radius: 5px;
	padding-bottom: 54px;
}
.dialog.mobile header {
	position: absolute;
	bottom: 0px;
	right: 5px;
	left: 5px;
	background-image: none;
}
@media screen and (max-width:576px) {
	#header.light nav a.logo, #header.dark nav a.logo {
		background-position: -2px -4px;
	}
	#header.light nav ul.links li.whatismeh a i,
	#header.dark nav ul.links li.whatismeh a i {
		background-position: -289px 0;
	}
	#header.light nav ul.links li.account a.anonymous i,
	#header.dark nav ul.links li.account a.anonymous i {
		background-position: -168px 0;
	}
	#header.light nav ul.links li.account a.authenticated i,
	#header.dark nav ul.links li.account a.authenticated i {
		background-position: -144px 0;
	}
	#header.light nav ul.links li.vmp a i,
	#header.dark nav ul.links li.vmp a i {
		background-position: -120px 0;
	}
	#header.light nav ul.links li.past-deals a i,
	#header.dark nav ul.links li.past-deals a i {
		background-position: -312px 0;
	}
	#header.light nav ul.links li.forum a i,
	#header.dark nav ul.links li.forum a i {
		background-position: -240px 0;
	}
	.image-uploader-wrapper {
		width: 90%;
		margin-left: -45%;
		min-height: 172px;
		margin-top: -105px;
	}
	.image-uploader .drag-instructions {
		display: none;
	}
	.image-uploader #uploader-button {
		margin: 0;
	}
	.image-uploader .image-selector .or {
		margin-top: inherit;
	}
}
}
