css:
/* Originally created by nanalan 
   Converted to userstyle by PullJosh */

@-moz-document url("https://scratch.mit.edu/") {
#navigation {
    border-bottom: none;
}
#navigation .logo a {
    -webkit-transition: none;
    transition: none;
    background-size: 81px !important;
}
#navigation .link a span {
    font-weight: 400;
    font-size: 14px;
}
#navigation .mystuff > a,
#navigation .messages > a {
    background-size: 24px !important;
}
body {
    font-family: Open Sans, Roboto, Fira Sans, Helvetica Neue, Helvetica, Arial, sans-serif;
}
body::-moz-selection {
    color: #fff;
    background-color: #f90;
}
body::selection {
    color: #fff;
    background-color: #f90;
}
.comment.removed {
    display: none;
}
.reply[style="display: list-item;"] {
    display: none !important;
}
* {
    text-shadow: none !important;
}
}

@-moz-document regexp("https:\\/\\/scratch\\.mit\\.edu\\/.+") {
body:not(.editor) {
    background: #fff;
    color: #000;
    height: auto;
    min-height: 100%;
}
body:not(.editor) .avatar img {
    border-radius: 3px;
}
body:not(.editor) #content {
    padding-top: 6em;
}
body:not(.editor) #topnav {
    height: 51px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    position: fixed;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.25);
}
body:not(.editor) #topnav .innerwrap {
    height: 100%;
    background-color: #25aff4;
}
body:not(.editor) #topnav .innerwrap .container {
    height: 100%;
    width: 942px;
    margin: 0 auto;
}
body:not(.editor) #topnav .innerwrap .container li {
    border: none;
    height: 51px;
    line-height: 51px;
    background: transparent;
}
body:not(.editor) #topnav .innerwrap .container li:hover {
    background: rgba(0, 0, 0, 0.1);
}
body:not(.editor) #topnav .innerwrap .container > a,
body:not(.editor) #topnav .innerwrap .container .site-nav > li > a,
body:not(.editor) #topnav .innerwrap .container .account-nav > li > a {
    position: relative;
    border: none;
    color: #fff;
    font-size: 14px;
    margin: 0;
    padding: 17px 15px 0;
    height: 33px;
    line-height: 1;
    font-weight: 400;
}
body:not(.editor) #topnav .innerwrap .container .logo {
    width: 80px;
    -webkit-flex-shrink: 0;
    -ms-flex-negative: 0;
    flex-shrink: 0;
    background-image: url("https://scratch.mit.edu/images/logo_sm.png");
    background-repeat: no-repeat;
    background-size: 81px;
    background-position: center;
    padding-right: 16px;
}
body:not(.editor) #topnav .innerwrap .container .logo span {
    display: none;
}
body:not(.editor) #topnav .innerwrap .account-nav {
    margin: 0;
    height: 51px;
}
body:not(.editor) #topnav .innerwrap .account-nav .logged-in-user.open {
    background-color: transparent !important;
}
body:not(.editor) #topnav .innerwrap .account-nav.logged-in > li .dropdown-menu {
    background-color: #25aff4;
    margin-top: 25px;
    box-shadow: none;
}
body:not(.editor) #topnav .innerwrap .account-nav.logged-in > li .dropdown-menu li,
body:not(.editor) #topnav .innerwrap .account-nav.logged-in > li .dropdown-menu li > a {
    height: 36px;
    line-height: 36px;
}
body:not(.editor) #topnav .innerwrap .account-nav.logged-in > li .dropdown-menu li form input[type=submit],
body:not(.editor) #topnav .innerwrap .account-nav.logged-in > li .dropdown-menu li > a form input[type=submit] {
    background-color: transparent;
}
body:not(.editor) #topnav .innerwrap .account-nav.logged-in > li > .messages-icon {
    background: url("https://scratch.mit.edu/images/nav-notifications.png") no-repeat center;
    background-size: 24px;
    padding-left: 10px;
    width: 30px;
    padding-right: 10px;
}
body:not(.editor) #topnav .innerwrap .account-nav.logged-in > li > .mystuff-icon {
    background: url("https://scratch.mit.edu/images/mystuff.png") no-repeat center;
    background-size: 24px;
    padding-left: 10px;
    padding-right: 10px;
    width: 30px;
}
body:not(.editor) #topnav .innerwrap .account-nav.logged-in > li .notificationsCount {
    line-height: 17px;
    margin-top: 4px;
}
body:not(.editor) #topnav .innerwrap .account-nav.logged-in > li .user-icon {
    border: none;
    border-radius: 3px;
    margin-top: 3px;
}
body:not(.editor) #topnav .innerwrap .account-nav.logged-in > li .caret {
    border: none;
    background: url("https://scratch.mit.edu/images/dropdown.png") no-repeat center;
    height: 20px;
    width: 20px;
    margin-left: 8px;
    background-size: 50%;
    opacity: 1;
}
body:not(.editor) #topnav form.search {
    margin: 0 20px;
}
body:not(.editor) #topnav form.search .glass {
    position: absolute;
    background-color: transparent;
    background-image: url("https://scratch.mit.edu/images/nav-search-glass.png");
    background-repeat: no-repeat;
    background-position: center;
    background-size: 14px 14px;
    width: 40px;
    border: none;
    box-shadow: none;
    margin-top: 5px;
    cursor: pointer;
    height: 40px;
}
body:not(.editor) #topnav form.search #search-input {
    -webkit-transition: 0.15s ease background-color;
    transition: 0.15s ease background-color;
    padding: 0;
    padding-right: 10px;
    padding-left: 40px;
    height: 40px;
    color: #fff;
    font-size: 14px;
    outline: none;
    display: inline-block;
    margin: 0.5em 0;
    margin-top: 5px;
    box-shadow: none;
    border: none;
    background-color: rgba(0, 0, 0, 0.1);
    border-radius: 5px;
    width: 147.281px;
    -webkit-box-flex: 0;
    -webkit-flex-grow: 0;
    -ms-flex-positive: 0;
    flex-grow: 0;
}
body:not(.editor) #topnav form.search #search-input:focus {
    background-color: rgba(0, 0, 0, 0.2);
}
body:not(.editor) #topnav form.search::-webkit-input-placeholder {
    color: rgba(255, 255, 255, 0.75);
}
body:not(.editor) #topnav form.search::-moz-placeholder {
    color: rgba(255, 255, 255, 0.75);
}
body:not(.editor) #topnav form.search:-ms-input-placeholder {
    color: rgba(255, 255, 255, 0.75);
}
body:not(.editor) #topnav form.search::placeholder {
    color: rgba(255, 255, 255, 0.75);
}
body:not(.editor) a:not(.button),
body:not(.editor) input.link {
    cursor: pointer;
}
body:not(.editor) a:not(.button):hover,
body:not(.editor) input.link:hover {
    text-decoration: none;
}
body:not(.editor) .user.thumb img {
    border: none;
    border-radius: 3px;
}
body:not(.editor) .avatar {
    border-radius: 3px;
}
body:not(.editor) .box .box-head,
body:not(.editor) h2,
body:not(.editor) h3,
body:not(.editor) h4,
body:not(.editor) h1,
body:not(.editor) .sub-list a,
body:not(.editor) #tabs a {
    color: #6b6b6b;
    font-weight: 400;
}
body:not(.editor) .box {
    box-shadow: none;
}
body:not(.editor) #tabs {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
body:not(.editor) #tabs .active {
    background-color: #25aff4;
    text-shadow: none;
}
body:not(.editor) #tabs .active a {
    color: #fff !important;
    text-shadow: none;
}
body:not(.editor) .dropdown .dropdown-menu li {
    color: #000;
}
body:not(.editor) .button,
body:not(.editor) .button.grey,
body:not(.editor) button {
    border: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-image: none;
    background-color: #607d8b;
    border-radius: 3px;
    outline: 0;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
    color: #fff !important;
    text-transform: capitalize;
    text-shadow: none;
    padding: 0 1rem;
    min-height: 32px;
    -webkit-transition: box-shadow 0.3s ease-out;
    transition: box-shadow 0.3s ease-out;
}
body:not(.editor) .button span.dropdown-toggle.black span.caret,
body:not(.editor) .button.grey span.dropdown-toggle.black span.caret,
body:not(.editor) button span.dropdown-toggle.black span.caret {
    border-top: 4px solid #fff;
}
body:not(.editor) .button span.black,
body:not(.editor) .button.grey span.black,
body:not(.editor) button span.black {
    color: #fff;
    -webkit-filter: brightness(10);
    filter: brightness(10);
}
body:not(.editor) .button[data-control="load-more"],
body:not(.editor) .button.grey[data-control="load-more"],
body:not(.editor) button[data-control="load-more"] {
    margin-bottom: 20px;
}
body:not(.editor) .button.small,
body:not(.editor) .button.grey.small,
body:not(.editor) button.small {
    padding: 0 0.5rem;
}
body:not(.editor) .button a,
body:not(.editor) .button.grey a,
body:not(.editor) button a,
body:not(.editor) .button span,
body:not(.editor) .button.grey span,
body:not(.editor) button span {
    color: #fff;
    letter-spacing: 0.1px;
}
body:not(.editor) .button:focus,
body:not(.editor) .button.grey:focus,
body:not(.editor) button:focus,
body:not(.editor) .button:active,
body:not(.editor) .button.grey:active,
body:not(.editor) button:active {
    box-shadow: none;
}
body:not(.editor) .button:hover,
body:not(.editor) .button.grey:hover,
body:not(.editor) button:hover {
    box-shadow: 0 5px 11px 0 rgba(0, 0, 0, 0.18), 0 4px 15px 0 rgba(0, 0, 0, 0.15);
}
body:not(.editor) .button.blue,
body:not(.editor) .button.grey.blue,
body:not(.editor) button.blue,
body:not(.editor) .button#see-inside,
body:not(.editor) .button.grey#see-inside,
body:not(.editor) button#see-inside,
body:not(.editor) .button[data-control="post"],
body:not(.editor) .button.grey[data-control="post"],
body:not(.editor) button[data-control="post"],
body:not(.editor) .button[data-control="edit"],
body:not(.editor) .button.grey[data-control="edit"],
body:not(.editor) button[data-control="edit"] {
    background-color: #25aff4;
}
body:not(.editor) .button#see-inside,
body:not(.editor) .button.grey#see-inside,
body:not(.editor) button#see-inside {
    padding-left: 0.5rem;
    padding-right: 0.5rem;
}
body:not(.editor) .button.orange,
body:not(.editor) .button.grey.orange,
body:not(.editor) button.orange,
body:not(.editor) .button#share,
body:not(.editor) .button.grey#share,
body:not(.editor) button#share,
body:not(.editor) .button[href="/projects/editor/"],
body:not(.editor) .button.grey[href="/projects/editor/"],
body:not(.editor) button[href="/projects/editor/"] {
    background-color: #f90;
}
body:not(.editor) #share-bar {
    border: 1px solid rgba(255, 153, 0, 0.3);
    background-color: rgba(255, 153, 0, 0.2);
    font-weight: 400;
}
body {
    font-family: Open Sans, Roboto, Fira Sans, Helvetica Neue, Helvetica, Arial, sans-serif;
}
body::-moz-selection {
    color: #fff;
    background-color: #f90;
}
body::selection {
    color: #fff;
    background-color: #f90;
}
.comment.removed {
    display: none;
}
.reply[style="display: list-item;"] {
    display: none !important;
}
* {
    text-shadow: none !important;
}
}
