@-moz-document domain("carrot.com") {
/*code by letrainfalldown*/

.top-area {
    background-color: #2a80b9 !important;
/*     height: 0px; */
/*     display: none; */
    z-index: 100;
}

.account-box__speaker {
    background:#2a80b9;
    transition: 0.3s;
}

.account-box__speaker:hover {
    background:#539fd2;
    transition: 0.3s;
}

