.aside {
    background-color: #4d394b !important;
    top: 0px !important;
}

.aside::-webkit-scrollbar {
    background: #4d394b !important;
}

.aside::-webkit-scrollbar-thumb {
    border-color: #4d394b !important;
}

.aside .cell--active {
    background-color: #4c9689;
	color: #ffffff !important;
}

.aside .cell--active .cell__content {
	background-color: #4c9689 !important;
}

.aside .cell__content:hover, .cells-app-ios:hover, .cells-app-android:hover {
    background: rgba(0,0,0, 0.3) !important;
}

.cell__accessory__badge {
/*     background-color: rgba(214,34,34, 0.7);#d62222;*/
    border-radius: 20px;
/*     padding: 2px; */
    color: rgba(255,255,255, 0.8);
} 

.cell__accessory__badge--mention {
    background-color: rgba(214,34,34, 0.7) !important;
    border-radius: 10px !important;
    padding: 3px 8px !important;
    color: #ffffff;
}

