style_setting_options.value:
.top-area, .account-box__speakers {
    height: 0px;
    display: none;
}

.app-handler-body {
    top: 0px;
}

.layout-aside {
    top: 0px;
}

.chat-window__controls {
    height: 110% !important;
}

.cell__accessory__badge--mention {
    background-color: rgba(214,34,34, 0.7) !important;
    border-radius: 10px !important;
    padding: 3px 8px !important;
    color: #ffffff;
}

.replybox__input-wrapper--bottom-fixed.replybox__input-wrapper {
    position: absolute !important;
	bottom: -70px !important;
}

.chat-window.chat-window--connected {
    height: 90%;
}

.chat-thread {
    height: 93%;
}

.search {
    position: relative;
	left: 10px !important;
}
