css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("warframe.market") {
    .hidden-description, [aria-label="Information Baners"], [aria-label="News and Notifications"], #you-orders-table-sell>caption[id^="tablesorter"], #sell-table>caption, #buy-table>caption, hr, .user-ingame-switch-label, .search-exist, .input-group-addon, .dropdown-menu .divider, .patreon-row, .online-counter, .footer-navs, .rights, .google-stuff{
        display:none;
        visibility:collapse;
    }
    .nav > li > a, .img-thumbnail, .form-control, .fade, .thumbnail, .progress-bar, .modal.fade .modal-dialog, .carousel-inner > .item, .swiper-container-free-mode > .swiper-wrapper, .swiper-wrapper, .swiper-pagination, #account-cont .tutor_list div.tutor-item, #account-cont .img-thumbnail, .pagination li a, .pagination .active a, .btn, .market-header, .chat-user-list ul li, .chat-user-list ul .menu-marker, .collapsing, .modal.fade .modal-dialog, .modal.in .modal-dialog, .carousel-inner > .item.next, .carousel-inner > .item.active.right, .carousel-inner > .item.prev, .carousel-inner > .item.active.left, .carousel-inner > .item.next.left, .carousel-inner > .item.prev.right, .carousel-inner > .item.active, .swiper-container-android .swiper-slide, .swiper-wrapper, .swiper-pagination, .swiper-container-vertical > .swiper-pagination, #account-cont .tutor_images img:hover, .chat-user-list ul .menu-marker{
        transition:none !important;
        -moz-transition:none !important;
        transform:unset !important;
        -moz-transform:unset !important;
    }
    a{
        outline:none !important;
    }
    .w-row, .inner-blocks, .nav-pills > li > a, .latest-load-more, a, .white_field, #account-cont .white_field_less, .table > thead > tr > th, .table > thead > tr > td, .table > tbody > tr > th, .table > tbody > tr > td, .table > tfoot > tr > th, .table > tfoot > tr > td, .nav > li > a:hover, .nav > li > a:focus, .w-form-group, .no-chat-list, .no-chat, #account-cont .vercode, #account-cont .tutor_list div.compose-message, .btn-group.open .dropdown-toggle{
        background:none transparent;
        border-color:transparent;
        box-shadow:none;
        outline:none transparent;
    }
    .chat-user-list ul li, .chat-user-list ul li.active, .chat-box .chat-dialogue-box, .chat-user-list{
        border-color:transparent;
    }
    html, body, caption, #w-message-box-out .w-message-box-cont, #w-message-box-out .w-message-box-cont .bg-success{
        background:none #1f1f1f;
        color:#dddddd;
    }
    #w-message-box-out .w-message-box-cont{
        height:100px;
    }
    #place-order-form .input-group, #place-order-form .input-group .twitter-typeahead input{
        width:450px;
    }
    thead, .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus, #account-cont .vercode, #account-cont .tutor_list div.tutor-item:hover, .form-control, .tt-dropdown-menu{
        background:none #111111;
        color:#dddddd;
        box-shadow:0px 1px 1px 0px #000000;
    }
    .tt-dropdown-menu, .tablesorter-header{
        cursor:pointer;
    }
    .footer, .footer-bord, .footer-bord .flex-footer{
        height:0;
        margin:0;
        padding:0;
        min-height:unset;
    }
    .inner-blocks, .w-navs, .white_field, #account-cont .white_field_less{
        padding:0;
    }
    .w-navs, .btn-group .btn + .btn, .btn-group .btn + .btn-group, .btn-group .btn-group + .btn, .btn-group .btn-group + .btn-group{
        margin:0;
    }
    .inner-blocks{
        margin-top:5px;
    }
    .collapse-btn{
        top:0;
        right:0;
    }
    .wrapper{
        padding-top:20px;
        padding-bottom:0;
    }
    .w-wrapper{
        padding-bottom:0;
    }
    .row.w-row{
        position:fixed;
        top:0;
        left:0;
        background:none #111111;
        width:100%;
        margin:0;
        padding:2px 0;
        height:30px;
        z-index:999;
        box-shadow:0px 1px 1px 0px #000000;
    }
    .user-ingame-switch-cont{
        position:fixed;
        top:0;
        margin:0 auto;
        left:25%;
        z-index:9999;
    }
    .w-logo{
        height:24px;
    }
    .nav-tabs{
        border:none;
    }
    .btn, .nav > li > a, .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus, .w-navs .nav-pills > li > a, .form-control, .inner-blocks .search-item, .nav-pills > li.active > a, .nav-pills > li.active > a:hover, .nav-pills > li.active > a:focus, .nav .open > a, .nav .open > a:hover, .nav .open > a:focus, .latest-orders-controll .btn-group-status .btn-success, .latest-load-more, .latest-load-reset, .footer-bord .btn-success, .footer-bord .btn-warning, .btn-group{
        font-size:12px;
        padding:0 10px;
        border-radius:2px;
        line-height:24px;
        height:24px;
        border:none;
        color:#dddddd;
        font-weight:bold;
        text-shadow:0px 2px 0px #000000, 0px -1px 1px #111111;
    }
    #place-order > div .btn-primary, #sign-in-area .btn-primary{
        color:#dddddd;
        font-weight:bold;
        text-shadow:0px 2px 0px #000000, 0px -1px 1px #000000;
    }
    .footer-bord .user-ingame-switch-cont{
        line-height:30px;
    }
    .nav-pills > li.active > a, .nav-pills > li.active > a:hover, .nav-pills > li.active > a:focus, .nav > li > a:hover, .nav > li > a:focus, .nav .open > a, .nav .open > a:hover, .nav .open > a:focus, .dropdown-menu, .dropdown-menu > li > a, .chat-user-list ul li:hover{
        background:none #313131;
        color:#dddddd;
    }
    .table-striped > tbody > tr:nth-child(2n+1), .latest-order-cont .latest-order-unit:nth-child(2n+1){
        background:none #212121;
        color:#dddddd
    }
    .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus, .table-striped > tbody > tr:nth-child(2n), .latest-order-cont .latest-order-unit:nth-child(2n), #account-cont .tutor_list div.compose-message, .chat-user-list ul li:nth-child(2n+1), .chat-box .chat-message{
        background:none #282828;
        color:#dddddd;
    }
    #w-message-box-out .w-message-box-cont .bg-danger{
        background-color:transparent;
    }
    [role="presentation"] > .dropdown-menu{
        position:absolute;
        top:30px;
        z-index:999;
        margin:0 0 0 -20px;
        padding:0;
        width:120px;
        min-width:unset;
        font-size:12px;
    }
    .chat-box .text-right{
        background-color:#313131 !important;
    }
    .btn-group.open .dropdown-toggle, #w-message-box-out .w-message-box-cont, .form-control:focus{
        box-shadow:none;
    }
    .dropdown-menu.inner{
    }
    .dropdown-menu > li{
/*         margin:0px; */
/*         padding:0; */
    }
    .dropdown-menu > li > a{
        padding:5px 10px;
    }
    .badge{
        border-radius:2px;
    }
    .btn-default, .btn-success, .btn-warning, .btn-danger{
        height:24px;
        line-height:24px;
    }
    .btn-success{
/*         background:none #5cb85c; */
        background:none #008a00;
        color:#dddddd;
    }
    .btn-success:hover, .btn-success:focus, .btn-success.focus, .btn-success:active, .btn-success.active, .open > .btn-success.dropdown-toggle{
        background:none #008a00;
        box-shadow:inset 0px 0px 2px 1px #1f1f1f;
        color:#ffffff;
    }
    .btn-default{
        background:none #313131;
        color:#ffffff;
    }
    .btn-default:hover, .btn-default:focus, .btn-default.focus, .btn-default:active, .btn-default.active, .open > .btn-default.dropdown-toggle{
        background:none #ff6b00;
        color:#dddddd;
    }
    .chat-user-list ul li.active span.status-online-ingame{
        color:#008a00;
        font-weight:bold;
        text-shadow:0px 0px 1px #008a00;
    }
    #place-order > div .btn-primary, #sign-in-area .btn-primary{
        width:100px;
    }
    .footer-bord .btn-success, .footer-bord .btn-warning{
        background:none #313131;
    }
    .footer-bord .btn-success.active, span#eta-update{
        background:none #008a00;
        box-shadow:inset 0 0 3px 1px #1f1f1f;
    }
    .footer-bord .btn-warning.active{
        background:none #f00000;
        box-shadow:inset 0 0 3px 1px #1f1f1f;
    }
    .table, .latest-order-cont{
        margin-bottom:10px;
    }
    input, .inner-blocks .search-item, .form-control, .twitter-typeahead .tt-dropdown-menu .tt-dataset-all_items{
        font-weight:normal;
        box-shadow:none;
        background:none #2f2f2f;
        padding:0;
    }
    .form-control{
        text-indent:10px;
    }
    .form-control:focus{
        background-color:#ff6b00 !important;
        color:#000000;
        text-shadow:none;
        font-weight:900;
    }
    tr.success > td{
        color:#1f1f1f;
    }
    tr.success > td.status-online-ingame, .status-online-ingame{
        color:#008a00;
        text-shadow:0px 0px 0px #008a00;
    }
    tr.success > td.status-online{
        color:#ff6b00;
        text-shadow:0px 0px 0px #ff6b00;
    }
    tr > td.status-offline{
        color:darkred;
        text-shadow:0px 0px 0px darkred;
    }
    .chat-user-list ul li.active{
        background:none #1b1b1b;
        border-left:5px solid #bdccdc;
    }
    .chat-box{
        background:#1b1b1b;
    }
    .chat-user-list ul .menu-marker{
        background:#bdccdc none repeat scroll 0 0 !important;
        border:none !important;
    }
}
