css:
@-moz-document domain("psnprofiles.com") {
html, body, .leaderboard .total, #leaderboard big, .leaderboard.zebra big, div.title-bar.description span, ul.trophy_count .level, .leaderboard .points {
    color: #bbb;
}
html body#ipboard_body {
    color: #000;
}
.small_info, .small-info {
    color: #CCC;
}
#content.page {
    background: #4F4F4F;
}
.element {
    background: #000;
    border-color: #999;
}
table.zebra tr:nth-child(odd), ul.zebra li:nth-child(odd) {
    background: rgb(58, 58, 58);
}
table.zebra tr:nth-child(even), ul.zebra li:nth-child(even), .odd, .box, #facebox .inner, #facebox div.title-bar {
    background: rgb(51, 51, 51);
}
#trophyTips .element .box {
    background: #333 !important;
}
.col-xs-6 tr.me {
    background: #232 !important;
}
tr.completed td {
    background: rgb(45, 60, 51)!important;
}
tr.platinum td {
    background: rgb(45, 51, 70)!important;
}
.xs-hide.lg-show a, .typo-top, .typo-bottom {
    color: #ccc;
}
.xs-hide.lg-show a:hover {
	color: #fff!important;
}
.zebra a, #slider .element .box table a, .ellipsis a {
    color: #7ba5e4 !important;
    text-shadow: 0.5px 0.5px #000;
}
.zebra a:hover, #slider .element .box table a:hover, .ellipsis a:hover {
    color: #fff !important;
}
table.zebra td img[src="/lib/img/icons/platinum_icon_off.png"], table.zebra td img[src="/lib/img/icons/complete_icon_off.png"] {
    opacity: 0.5;
}
.gr__psnprofiles_com > body > div.title-bar, #banner div.title-bar, .title-bar.width.no-banner {
    background: rgba(32, 32, 32, 0.88) !important;
}
#banner.small div.title-bar {
    background: none!important;
}
#reCaptcha > div {
    margin: 0 auto;
}
div.title-bar h3 {
    color: #f3f3f3;
}
div.title-bar .navigation li a {
    color: #CCC;
}
div.title-bar .navigation li.active a, div.title-bar .navigation li a:hover {
    color: #FFF;
}
div.title-bar .navigation li a, div.title-bar h3 a, div.title-bar h3 {
    text-shadow: none;
}
.sidebar.large .element.no-top-radius .box table:nth-child(2) {
    background: #222!important;
}
#facebox .title-bar h1 {
    color: #fff !important;
    text-shadow: 1px 1px black !important;
}
#facebox .content .bottom {
    background: #222;
}
.box {
    border-color: #898989;
}
.leaderboard-top {
    background: #222;
}
div.leaderboard-top .tab {
    background: #111;
}
div.leaderboard-top .tab.active, div.leaderboard-top .tab:hover {
    background: #333;
    color: #fff;
}
#messages .guide, a > img.trophy {
    background: transparent;
}
.session.boosting {
    color: #056900;
}
.button.red,.button.green {
    color: #fff!important;
}
.box.information,.box.info {
    background: #282a2d!important;
}


/* Guides */

.guide, .box.light, .custom-theme.fr-box.fr-basic .fr-wrapper {
    background: #222;
}
.roadmap-trophies .trophy.earned, .tableofcontents li.earned {
    background: #131 !important;
}
.tableofcontents li, .tableofcontents li.earned {
    border-top-color: #898989 !important;
    border-bottom-color: #898989 !important;
}
.roadmap-trophies .trophy {
    background: rgba(255, 255, 255, 0.02);
}
.roadmap-trophies li span, .tableofcontents li span, .guide, .custom-theme.fr-box.fr-basic .fr-element {
    color: #ddd;
}
.guide [style="color:rgb(0,0,0);text-align:justify;"] {
    color: #ddd!important;
}
#Roadmap h1, .guide h1 {
    color: #fff;
}
.guide h2 {
    color: #eee;
}
.CodeMirror {
    color: #ddd;
    background: #222;
}
.guide table {
    background: #303030;
}
.guide table tr:nth-child(even) td {
    background: #2A2A2A;
}
.guide table tr td, .guide table th, .guide table.invisible tr td, .guide table.invisible th {
    color: #ccc;
}
.guide table th {
    background: #222;
}
.guide a:hover {
    color: #fff;
}
#messageList .message > div.guide {
    color: #ddd;
    background: #222;
}
.guide blockquote {
    margin: 10px 10px 5px;
    font-style: italic;
}
}
