#page-wrap {
    font-family: 'Lato', sans-serif;
}
body {
    background-image: url('http://i.imgur.com/K24UlT2.jpg');
    background-repeat: repeat;
}
.container .container_content {
    background-image: none;
    background-color: #020302;
}
#page-footer,
.time-info,
.nameicons,
.m_forumlatestthreads .post-info .info + a.forum,
div[data-container-id="31291652"],
div[data-container-id="46320384"],
div[data-container-id="46320383"],
div[data-container-id="11481157"],
div[data-container-id="44325762"],
#enjin-bar,
.m_header object {
    display: none;
}
.m_header .holder {
    background: #000 url('http://i.imgur.com/dqp1uxL.jpg') no-repeat;
}
.special_container {
    margin-top: 5px;
    margin-bottom: 5px;
}
.m_hmenu a:hover .content,
.m_hmenu a:focus .content {
    text-shadow: 1px 1px 20px #78FB13;
}
.m_hmenu a:hover .b.left,
.m_hmenu a:hover .b.right,
.m_hmenu a:focus .b.left,
.m_hmenu a:focus .b.right {
    background: none;
}
.m_forumlatestthreads a:hover,
.m_forumlatestthreads a.old:hover,
.m_forumlatestthreads a.new:hover {
    color: #84C520;
    ;
}
.m_forumlatestthreads a,
.m_forumlatestthreads a.old,
.m_forumlatestthreads a.new {
    color: #bfbfbf;
}
.m_forumlatestthreads .thread .info {
    color: #666;
}
a {
    color: #6B9C20;
}
a:hover,
a:focus {
    color: #84C520;
}
}
