css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("oploverz.net") {
  
.recommended {display:none;
}
.postbody .ldr {display:none;
}
.postbody .box {display:none;
}

.postbody .boxed .left {display:none;
}
  
.center {
	max-width:1220px;
	margin:0 auto;
}

.strip {display:none;
}
#content {
	overflow:hidden;
	width:1200px;
	padding:15px;
	margin:0 auto;
	background:#FFF;
	border:1px solid;
	border-color:#e5e6e9 #dfe0e4 #d0d1d5;
	border-radius:3px;
}

#header .headads {display:none;
}

.postbody {
	width:1200px;
	float:center;
	margin-right:0px;
}

.postbody .boxed .right .lts {
	float:center;
	width:1200px;
}

.postbody .boxed .right .lts ul li .dtl {
	width:1040px;
	padding:5px;
	background:#F5F4F4;
	height:70px;
	float:right;
}

.animeinfos {
	max-width:1190px;
	float:left;
	margin-bottom:10px;
	overflow:hidden;
	line-height:20px;
}

.animeinfos .episodelist .offzone .lefttitle {
	padding:0 7px;
	line-height:30px;
	width:800px;
	text-align:left;
	display:block;
	margin-right:1px;
	float:left;
	background:#333;
	color:#FFF;
}

.animeinfos .episodelist .offzone .rightoff {
	padding:0 7px;
	line-height:30px;
	float:left;
	display:block;
	width:150px;
	margin-right:1px;
	text-align:left;
	background:#333;
	color:#FFF
}

.animeinfos .episodelist .offzone .watch {
	padding:0;
	line-height:30px;
	float:left;
	display:block;
	width:140px;
	text-align:center;
	background:#333;
	color:#FFF
}
  
.animeinfos .episodelist ul li .lefttitle {
	width:810px;
	display:block;
	overflow:hidden;
	text-overflow:ellipsis;
	white-space:nowrap;
	padding-left:5px;
	border-right:1px solid #FFF;
	float:left;
}
  
.animeinfos .episodelist ul li .rightoff {
	float:left;
	display:block;
	width:153px;
	border-right:1px solid #FFF;
	text-align:left;
	padding-left:10px
}
  
.animeinfos .episodelist ul li .watch {
	float:left;
	background:#F13E3E;
	display:block;
	width:140px;
	text-align:center
}  
  
#sidebar {display:none;
}
}
