@-moz-document regexp("https://(www|books|encrypted|images|videos).google.(com|co.[a-z]{2}|com.[a-z]{2}|[a-z]{2})?/(?!(maps)).*") {
/* front page & general*/

body.hp {
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0.6), rgba(255, 255, 255, 0.5) 43px, rgba(255, 255, 255, 0.4) 92px, rgba(255, 255, 255, 0.3) 146px, rgba(255, 255, 255, 0.2) 210px, rgba(255, 255, 255, 0.1) 295px, rgba(255, 255, 255, 0) 500px), url(https://source.unsplash.com/user/lh900519/likes/1920x1080)!important;
    height: 100%!important;
    background-position: center!important;
    /*background-size: cover;*/
    background-attachment: fixed;
    background-repeat: no-repeat;
    /*linear-gradient(to bottom, rgba(255, 255, 255, 0.6), rgba(255, 255, 255, 0.5) 43px, rgba(255, 255, 255, 0.4) 92px, rgba(255, 255, 255, 0.3) 146px, rgba(255, 255, 255, 0.2) 210px, rgba(255, 255, 255, 0.1) 295px, rgba(255, 255, 255, 0) 500px, rgba(255, 255, 255, 0))*/
    /*linear-gradient(to bottom, rgba(255, 255, 255, 0.8), rgba(255, 255, 255, 0.7) 32px, rgba(255, 255, 255, 0.6) 67px, rgba(255, 255, 255, 0.5) 104px, rgba(255, 255, 255, 0.4) 146px, rgba(255, 255, 255, 0.3) 194px,rgba(255, 255, 255, 0.2) 250px,rgba(255, 255, 255, 0.1) 323px, rgba(255, 255, 255, 0) 500px)*/
}

#hplogo {
    transition: 400ms;
    -webkit-filter: drop-shadow(0 2px 2px rgba(0,0,0,0.2))!important;
}

#main .logo-subtext>span {
    text-shadow: 0 0 10px rgba(255, 255, 255, 0.7)!important;
}
/*text-shadow*/

*:not([class*="icon"]):not(i):not(pre):not(code):not(kbd):not(samp):not(var) {
    font-family: "arial","sans-serif","Microsoft Yahei"!important;
    /*text-shadow: 0em 0em 0.03em #999999!important;*/
}

pre, code, kbd, samp, var {
    /*text-shadow: 0em 0em 0.03em #999999!important;*/
}
/*google speech*/

#spch {
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0.6), rgba(255, 255, 255, 0.5) 43px, rgba(255, 255, 255, 0.4) 92px, rgba(255, 255, 255, 0.3) 146px, rgba(255, 255, 255, 0.2) 210px, rgba(255, 255, 255, 0.1) 295px, rgba(255, 255, 255, 0) 500px, rgba(255, 255, 255, 0)), url(https://source.unsplash.com/user/lh900519/likes/1920x1080)!important;
    height: 100%!important;
    background-position: center!important;
    background-size: cover;
    background-attachment: fixed;
    background-repeat: no-repeat;
}

.hp ._gjb #spchi {
    color: white!important;
    text-shadow: 0 0 10px rgba(0,0,0,0.7), 0 0 8px rgba(0,0,0,0.5), 0 0 3px white!important;
}
.hp ._gjb #spchf {
    color: #f44!important;
    text-shadow: 0 0 10px rgba(0,0,0,0.7),0 0 8px rgba(0,0,0,0.5), 0 0 3px #f44!important;
}
.hp ._gjb #spchta {
    color: #C7DCFC!important;
    text-shadow: 0 0 10px rgba(0,0,0,0.7),0 0 8px rgba(0,0,0,0.5), 0 0 3px white!important;
}
/*front page foot*/

.hp #fbar {
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.1) 45%, rgba(0, 0, 0, 0.2) 63%, rgba(0, 0, 0, 0.3) 77%, rgba(0, 0, 0, 0.4) 90%,rgba(0, 0, 0, 0.5))!important;
    border: none!important;
}
.hp #fbar :not(#fsett):not(#advsl)>a {
    color: white!important;
    text-shadow: 0 0 8px #000, 0 0 4px #ddd!important;
    transition: 400ms!important;
}
.hp #fbar span {
    opacity: 1;
    transition: 400ms!important;
}
.hp #fbar:not(:hover) span {
    opacity: 0;
    transition-delay: 1s!important;
}
.hp #prm div, #prm a {
    text-shadow: 0 0 20px white,0 0 20px white,0 0 20px white,0 0 20px white,0 0 20px white!important;
}
.hp #prm img {
    -webkit-filter: drop-shadow(0 0 20px #fff)!important;
}
.hp #prm {
    transition:500ms;
    opacity:1;
}
.hp #prm:not(:hover) {
    opacity:0;
    transition-delay:1s;
}
/*search bar*/

#searchform {
    transition-duration: 0.2s;
}
.sfibbbc {
    animation: openVert 0.8s !important;
}
.srp #sbtc {
    transform:translatey(2px);
}
#sfdiv {
    transition: 500ms;
    height: 46px!important;
    border: none!important;
    border-radius: 4px!important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.2), 0 1px 5px 0 rgba(0, 0, 0, 0.12);
    top: 10px;
}

#sfdiv:first-child  {
    border-radius: 4px 0 0 4px!important;
}

.sbib_a, .sbibps {
    background: transparent!important;
    transition:none!important;
    transform:translatey(5px)!important;
    /*lst lst-tbb sbibps*/
}

#sfdiv.sbhcn {
    box-shadow: 0 3px 5px rgba(0, 0, 0, 0.20);
}

#sfdiv.sbfcn {
    box-shadow: 0 3px 5px rgba(0, 0, 0, 0.20);
}

.hp #sfdiv.sbhcn {
    box-shadow: 0 0 20px rgba(66, 133, 244, 0.8), 0 0 25px rgba(66, 133, 244, 1);
}

.hp #sfdiv.sbfcn {
    box-shadow: 0 0 10px rgba(66, 133, 244, 0.8), 0 0 15px rgba(66, 133, 244, 1);
}

.jsb input {
    display: none;
}

.srp #sfdiv {
    border-radius: 4px 0px 0px 4px!important;
}

#qbp {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.2), 0 1px 5px 0 rgba(0, 0, 0, 0.12)!important;
    border: none;
    border-radius: 3px;
    overflow: hidden;
    position:fixed;
    animation:fade 0.5s;
}

/*search form*/

.srp #searchform {
    position: fixed;
}

.sfbgg, body:not(.hp) .gb_re, body:not(.hp) #maia-header, .jfk-scrollbar .gb_se, #gb .gb_se, .jfk-scrollbar #gb .gb_ue {
    background: linear-gradient(to top, rgba(255, 255, 255, 1), rgba(255, 255, 255, 0.2)), url(https://source.unsplash.com/user/lh900519/likes/1920x1080), white!important;
    height: 70px!important;
    background-position: top center!important;
    background-size: cover;
    background-attachment: fixed;
    background-repeat: no-repeat;
    border: none;
    box-shadow: 0 0 15px 5px white!important;
}

#hdtb {
    margin-top: 10px;
}

.gb_Qb, a#logo, #maia-header img {
    -webkit-filter: drop-shadow(0 1px 1px rgba(0, 0, 0, 0.3));
}
a#logo, #maia-header img, .gb_kd {
    transform:translatey(5px)!important;
}

.gb_db {
    padding-top: 5px!important;
}

#sblsbb {
    text-align: center;
    border-bottom-left-radius: 0;
    border-top-left-radius: 0;
    height: 46px;
    margin: 0;
    padding: 0;
    width: 46px;
    min-width: 38px !important;
    background-color: #4285f4;
    background-image: none;
    border: none;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.2), 0 1px 5px 0 rgba(0, 0, 0, 0.12);
}

button.lsb {
    height: 46px!important;
}

.sbdd_a {
    top: 52px!important;
}

.sbdd_b {
    border-radius: 4px!important;
    border: none;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.2), 0 1px 5px 0 rgba(0, 0, 0, 0.12);
    overflow: hidden!important;
}

.sbdd_b li {
    height: 32px;
}

.sbdd_b li div {
    padding-top: 2px;
}
/*foot*/

.srp #footcnt {
    background: url(https://source.unsplash.com/user/lh900519/likes/1920x1080)!important;
    background-position: bottom center!important;
    background-size: cover;
    background-attachment: fixed!important;
    background-repeat: no-repeat;
    border: none;
    box-shadow: 0 0 15px 5px white!important;
}

.srp #fbar {
    background: linear-gradient(to bottom, rgba(255, 255, 255, 1), rgba(255, 255, 255, 0.2))!important;
    border: none;
}

.srp #fbar a, .srp #fbar span {
    text-shadow: 0 0 0.03em #999, 0 0 10px #fff, 0 0 10px #fff!important;
}
.srp #fbar div {
    opacity: 1;
    transition:0.5s;
}
.srp #fbar:not(:hover) div {
    opacity: 0;
    transition-delay: 1s!important;
}

/*animations*/

.gb_fb, .gb_eb, .gb_db, .gb_ha, .gb_bb, .gb_ab, .gb_ga, .hdtb-mn-o, .goog-menu, div[role="menu"], .d-Na-JG-M {
    animation: fadeInDown 0.3s !important;
    transition: none !important;
}

#lga {
    animation: fade 0.8s;
}

body, br, form, span, img, p, textfield, input {
    animation: none;
}
/*google image*/
/*thumbnails*/

.rg_el {
    animation: zoomCenter 0.6s!important;
}
/*cards*/

.kp-blk, .whenis, .fac-lstng, .tw-bil-e, .vk_c, ._M6k {
    animation: put 0.6s;
}

.rg_l, .kp-blk, .Ihwked, .whenis, .fac-lstng, .tw-bil-e, .vk_c, ._M6k {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.2), 0 1px 5px 0 rgba(0, 0, 0, 0.12)!important;
    border: none;
    border-radius: 3px;
    overflow: hidden;
    transition: 0.4s;
}

.rg_l:hover, .kp-blk:hover, .Ihwked:hover, .whenis:hover, .fac-lstng:hover, .tw-bil-e:hover, .vk_c:hover, ._M6k:hover {
    box-shadow: 0 3px 15px rgba(0,0,0,0.30)!important;
}

.rg_el, .kno-fb-ctx {
    overflow: visible!important;
}

.irc_mi {
    animation: none;
}

.rg_fbl:hover {
    border-radius: 3px;
}
}

@-moz-document domain("www.google.com"), regexp("https://(www|books|encrypted|images|videos).google.(com|co.[a-z]{2}|com.[a-z]{2}|[a-z]{2})?/.*") {
/*Animations for reference*/

@keyframes fadeInDown {
    0% {
        transform: translate(0px, -20px);
        opacity: 0;
    }
    100% {
        transform: translate(0px, 0px);
        opacity: 1;
    }
}

@keyframes fadeInRight {
    0% {
        transform: translate(-20px, 0px);
        opacity: 0;
    }
    100% {
        transform: translate(0px, 0px);
        opacity: 1;
    }
}

@keyframes fadeInUp {
    0% {
        transform: translate(0px, 20px);
        opacity: 0;
    }
    100% {
        transform: translate(0px, 0px);
        opacity: 1;
    }
}

@keyframes fadeInLeft {
    0% {
        transform: translate(20px, 0px);
        opacity: 0;
    }
    100% {
        transform: translate(0px, 0px);
        opacity: 1;
    }
}

@keyframes slideInDown {
    0% {
        transform: translateY(-50%);
    }
    100% {
        transform: translateY(0);
    }
}

@keyframes put {
    0% {
        transform: scale(1.1, 1.1);
        opacity: 0;
    }
    100% {
        transform: scale(1, 1);
        opacity: 1;
    }
}

@keyframes openHoriz {
    0% {
        transform: scale(1, 0.8);
        opacity: 0;
    }
    100% {
        transform: scale(1, 1);
        opacity: 1;
    }
}

@keyframes openVert {
    0% {
        transform: scale(0.8, 1);
        opacity: 0;
    }
    100% {
        transform: scale(1, 1);
        opacity: 1;
    }
}

@keyframes zoomCenter {
    0% {
        transform: scale(0.9, 0.9);
        opacity: 0;
    }
    100% {
        transform: scale(1, 1);
        opacity: 1;
    }
}

@keyframes zoomTopCenter {
    0% {
        transform: translate(0px, -100px) scale(0.9, 0.7);
        opacity: 0;
    }
    100% {
        transform: translate(0px, 0px) scale(1, 1);
        opacity: 1;
    }
}

@keyframes zoomBottomCenter {
    0% {
        transform: translate(0px, 100px) scale(0.9, 0.7);
        opacity: 0;
    }
    100% {
        transform: translate(0px, 0px) scale(1, 1);
        opacity: 1;
    }
}

@keyframes rollLeft {
    0% {
        transform: rotate(5deg);
        opacity: 0;
    }
    100% {
        transform: rotate(0deg);
        opacity: 1;
    }
}

@keyframes rollRight {
    0% {
        transform: rotate(-5deg);
        opacity: 0;
    }
    100% {
        transform: rotate(0deg);
        opacity: 1;
    }
}

@keyframes fade {
    0% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}

@keyframes logoR {
    0% {
        transform: translateY(0px);
    }
    50% {
        transform: translateY(5px);
    }
    100% {
        transform: translateY(0px);
        opacity: 1;
    }
}
}
