css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gameblog.fr") {
  #gb_header{
    margin-left: -40%;
    width: 80%; 
  }
  
  #gb_content_start{
   width: 80%;  
  }
  
  .review_score_block{
    width: 11%;
    margin: 15px 15px 0 0;
  }
  
  .review_score{
    float: right;
    width: 100%;
  }
  
  .review_text{
    width: 75%;
  }
  
  .gb_une.trial article,
  .gb_une.trial_dontmiss article{
   width: 32.7%;
   height: auto;
  }
  
  .gb_review_comm,
  #gb_gbtv_header,
  #filActu_actus > article:nth-child(21),
  #gb_home_topactu,
  #gb_home_communaute_topjeux,
  #gb_home_gros_plan,
  .bloc300,
  #gb_gbtv_header,
  .bloc300.sidebyside{
    display: none;
  }
  
  .gb_une.dontmiss article{
    height: auto;
    width: 49%;
  }
  
  #gb_home_actu_tri{
    position: absolute;
    top: 5px;
    right: -43px;
  }
  
  #filActu_actus article{
    width: 23.2%;
    float: none;
    display: inline-block;
    vertical-align: top;
  }
  
  .gb_actu4 article:nth-of-type(4n+1),
  .gb_actu3 article:nth-of-type(3n+1){
    margin: 0 0 15px 15px;
  }
  
  .dualcol .pad300{
    width: 100%;
  }
  
  .textcontent .chapeau{
    margin: 0;
  }
  
  article header h1{
    margin: 15px 0;
  }
  
  .pad300 .gb_actu3 article{
    width: 31%;
  }
  
  article footer{
    display: none;
  }
  
  .social_bar > div:not(.share_btn_comments),
  .social_bar > a{
    display: none;
  }
  
  .social_bar > div.share_btn_comments{
    margin: 0 auto;
    float: right;
    width: 25%;
    text-align: center;
    background-image: none;
    height: auto;
    font-weight: bold;
  }
  
  .social_bar > div.share_btn_comments span:before{
    content: "Tous les commentaires ";
    font-weight: normal;
  }
  
  .social_bar > div.share_btn_comments span{
    color: #FFF;
    font-size: 14px;
    position: relative;
    padding: 15px;
    left: 0;
  }
  
  #gb_home_encemoment{
    display: none;
  }
  
  footer.gb_wrapper > div > div:not(.mainlinks):not(.smaller){
    display: none;
  }
  
  #gb_header_bnr_gbtv,
  #cookies_notifier_container{
    display: none;
  }
}
